/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	gfn_showMsg("CI00001");

	var strCode = '';
	strCode = ds_cond.GetColumn(0, "GBN");
//alert(	strCode );

	if(strCode == 'M') 
	{		
		ds_sup_cd.Filter("CD=="+quote(strCode));
	}
	else if(strCode == 'E') 
	{
		ds_sup_cd.Filter("CD=="+quote(strCode));
	}
	else
	{
		ds_sup_cd.unFilter();
	}



}




