/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{
//alert("a");
	if(gfn_isNull(div_pop.cb_lifnr, true, div_pop.Static9.Text)) return;
	if(gfn_isNull(div_pop.cal_suprdt, true, div_pop.Static27.Text)) return;
	if(gfn_isNull(div_pop.ed_pjt, true, div_pop.Static1.Text)) return;		
	if(gfn_isNull(div_pop.ed_posid, true, div_pop.Static11.Text)) return;		
	if(gfn_isNull(div_pop.cb_gbn, true, div_pop.Static7.Text)) return;
	if(gfn_isNull(div_pop.ed_supman, true, div_pop.Static12.Text)) return;
	if(gfn_isNull(div_pop.cal_supdt, true, div_pop.Static14.Text)) return;			
	if(gfn_isNull(div_pop.cb_finish, true, div_pop.Static15.Text)) return;	
	//if(gfn_isNull(cal_supdt2, true, Static16.Text)) return;

	 if( ds_master.GetColumn(0, "FINISH") = 'B')
	 {
	   // global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
	   // global.FRM_BOTTOM.st_msg.color = "yellow";       
	   // global.FRM_BOTTOM.st_msg.value = "완료 시 저장이 가능합니다.";
		 var arr =["완료 시 저장이 가능합니다."];
		 gfn_showAlert("CW10025",arr);

	    return;
	 }
/*
    ds_chk.clearData();
	if(PER_CHK == "N")
	{

		tit_clearActionInfo();		
		tit_addSearchActionInfo("ps12:PS1204001B_S5"); 
		tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_chk=ds_chk"
				, ""
				, ""//"fn_afterQuery"
				, ""
				, ""
				, true);


	}

	if(ds_chk.GetRowCount() > 0)	
	{
		gfn_showMsg("CW00137");
		return;
	}
*/			
//alert("c");

	ds_save.ClearData();	
	ds_save.AddRow();	
	ds_save.SetColumn(0, "BIGO", ds_master.GetColumn(0, "BIGO"));
	ds_save.SetColumn(0, "BIGO2", ds_master.GetColumn(0, "BIGO2"));
	ds_save.SetColumn(0, "BIGO3", ds_master.GetColumn(0, "BIGO3"));
	ds_save.SetColumn(0, "BIGO4", ds_master.GetColumn(0, "BIGO4"));
	//ds_save.SetColumn(0, "BIGO_1", ds_master.GetColumn(0, "BIGO_1"));

	ds_save.SetColumn(0, "PROWRK", ds_master.GetColumn(0, "PROWRK"));
	ds_save.SetColumn(0, "SUP_CD", ds_master.GetColumn(0, "SUP_CD"));
	ds_save.SetColumn(0, "SUP_DT", ds_master.GetColumn(0, "SUP_DT"));
//	ds_save.SetColumn(0, "SUP_DT2", ds_master.GetColumn(0, "SUP_DT2"));
	ds_save.SetColumn(0, "SUP_MAN", ds_master.GetColumn(0, "SUP_MAN"));
	//ds_save.SetColumn(0, "SUP_MAN2", ds_master.GetColumn(0, "SUP_MAN2"));
	ds_save.SetColumn(0, "FINISH", ds_master.GetColumn(0, "FINISH"));

	ds_save.SetColumn(0, "JQPR", ds_master.GetColumn(0, "JQPR"));

	ds_save.SetColumn(0, "INVERTER_ER", ds_master.GetColumn(0, "INVERTER_ER"));
	ds_save.SetColumn(0, "INVERTER_ER2", ds_master.GetColumn(0, "INVERTER_ER2"));
	ds_save.SetColumn(0, "INVERTER_ER3", ds_master.GetColumn(0, "INVERTER_ER3"));
	ds_save.SetColumn(0, "INVERTER_VER", ds_master.GetColumn(0, "INVERTER_VER"));
	ds_save.SetColumn(0, "SYSTEM_ER", ds_master.GetColumn(0, "SYSTEM_ER"));
	ds_save.SetColumn(0, "SYSTEM_ER2", ds_master.GetColumn(0, "SYSTEM_ER2"));
	ds_save.SetColumn(0, "SYSTEM_ER3", ds_master.GetColumn(0, "SYSTEM_ER3"));
	ds_save.SetColumn(0, "SYSTEM_VER", ds_master.GetColumn(0, "SYSTEM_VER"));
	ds_save.SetColumn(0, "CTC_ER", ds_master.GetColumn(0, "CTC_ER"));
	ds_save.SetColumn(0, "CTC_ER2", ds_master.GetColumn(0, "CTC_ER2"));
	ds_save.SetColumn(0, "CTC_ER3", ds_master.GetColumn(0, "CTC_ER3"));
	ds_save.SetColumn(0, "CTC_VER", ds_master.GetColumn(0, "CTC_VER"));

	var lv_mh = 0.0;
	if (length(trim(ds_master.GetColumn(0, "MH"))) == 0)
	{
		lv_mh = 0.0;
	}
	else
	{
		lv_mh = ds_master.GetColumn(0, "MH");
	}
	//ds_save.SetColumn(0, "MH", ds_master.GetColumn(0, "MH"));
	ds_save.SetColumn(0, "MH", lv_mh);


	ds_save.SetColumn(0, "PSPID", ds_cond.GetColumn(0, "PSPID"));
	ds_save.SetColumn(0, "POSID", ds_cond.GetColumn(0, "POSID"));	
	ds_save.SetColumn(0, "GBN", ds_cond.GetColumn(0, "GBN"));
	ds_save.SetColumn(0, "LIFNR", ds_cond.GetColumn(0, "LIFNR"));
	ds_save.SetColumn(0, "SUPR_DT", ds_cond.GetColumn(0, "SUPR_DT"));
	ds_save.SetColumn(0, "DPT", ds_master.GetColumn(0, "DPT"));
	ds_save.SetColumn(0, "TEL_SPT", ds_master.GetColumn(0, "TEL_SPT"));
	ds_save.SetColumn(0, "OCC_CA", ds_master.GetColumn(0, "OCC_CA"));
	ds_save.SetColumn(0, "EL_SPT", ds_master.GetColumn(0, "EL_SPT"));





//alert("b");
	tit_clearActionInfo();		
	//tit_addSelectExActionInfo("ps12:PS1204001B_S6", "ps12:PS1204001B_I1", "ps12:PS1204001B_U1");
	tit_addSelectExActionInfo("ps12:PS1204002B_S4", "ps12:PS1204002B_I1", "ps12:PS1204002B_U1");
	tit_callService(
		""
		, ""
		, "ds_save=ds_save"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);





 //alert("요깅");
}



/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		//ufn_enable("C");
		//PER_CHK = 'C';  //저장시 변경으로 들어감
		gfn_showMsg("CI00003");		
		PER_CHK = 'B'; //저장후 기술지원보고서로 변경
		parent.fn_query();
	}
}





/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() 
{

	if(gfn_isNull(div_pop.cb_lifnr, true, div_pop.Static9.Text)) return false; 
	if(gfn_isNull(div_pop.cal_suprdt, true, div_pop.Static27.Text)) return false;
	if(gfn_isNull(div_pop.ed_pjt, true, div_pop.Static1.Text)) return false;
	if(gfn_isNull(div_pop.ed_posid, true, div_pop.Static11.Text)) return false;
	if(gfn_isNull(div_pop.cb_gbn, true, div_pop.Static7.Text)) return false;

	if(PER_CHK == "B")
	{
		if (!gfn_showConfirm("CI00015","기술지원 보고서")) 
		{
			return;
		}	


		tit_clearActionInfo();			
		//tit_addSingleActionInfo("ps12:PS1204001B_D1");
		tit_addSingleActionInfo("ps12:PS1204002B_D1");
		tit_callService(
			""
			, ""        
			, "ds_cond=ds_cond"
			, ""
			, ""
			, "fn_afterDelete"
			, ""
			, ""
			,true  // ""
			);	
      }
      else if(PER_CHK == "A")
      {
		//CW00022=[${}] 을/를 확인하십시요.
		gfn_showMsg("CW00022", '기술지원 보고서가 작성되지 않은 요청서입니다. 정보');
      }

	//fn_reset("D");

}




/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00004");		
		parent.fn_query();    
		fn_close();
	}
}




