function ufn_enable(chk)
{
 //alert("b");
	if(chk=="F")
	{ //alert("1");
//PRE_GRP
		cb_dept2.Enable 	= false;
	    ed_supman.Enable 	= false;
	    cb_dept.Enable   	= false;
		cal_suprdt.Enable 	= false;
		ed_pjt.Enable 		= false;
		btn_pjt.Enable      = false;
		cb_gbn.Enable 		= false;
	//	cb_sup_cd.Enable 	= false;
		//ta_bigo.Enable      = false;
		ta_bigo.Readonly    = true;
		ta_bigo2.Readonly    = true;
		ta_bigo3.Readonly    = true;
		//ed_supman.Enable    = false;
		//cal_supdt.Enable    = false;

		//ed_prwrk.Enable     = false;
		//cb_gbn2.Enable 		= false;
		Edit2.Enable        = false;
		Edit0.Enable		= false;
		//grd_list.Enable     = false;


		div_btn.btn_query.visible = false;
		div_btn.btn_delete.visible = false;
		div_btn.btn_save.visible = false;     
	}
	else if(chk=="I")
	{   	// alert("2");    

	    //cb_lifnr.Enable 	= true;
		//cal_suprdt.Enable 	= true;
		//ed_pjt.Enable 		= true;
		//btn_pjt.Enable      = true;
		//cb_gbn.Enable 		= true;
		//cb_sup_cd.Enable 	= true;
		//ta_bigo.Enable      = true;
	}
	else if(chk=="C")	
	{ //alert("3");

		if( PRE_GRP == '10' || PRE_GRP == '30' || PRE_GRP == '40') //설치PM 추가
		{
			cb_dept.Enable 	= false;
			ed_supman.Enable 	= true;
			cb_dept2.Enable 	= true;
			cal_suprdt.Enable 	= false;
			ed_pjt.Enable 		= false;
			btn_pjt.Enable      = false;
			cb_gbn.Enable 		= false;
			//cb_sup_cd.Enable 	= true;
			//ta_bigo.Enable      = true;
			ta_bigo.Readonly    = false;
			ta_bigo2.Readonly    = false;
			ta_bigo3.Enable     = false;

			//ed_supman.Enable    = false;
			//cal_supdt.Enable    = false;

			//ed_prwrk.Enable     = true;
			//cb_gbn2.Enable 		= true;

			Edit2.Enable        = false;
			Edit0.Enable		= false;
		//	grd_list.Enable     = true;


			div_btn.btn_query.visible = false;
			div_btn.btn_delete.visible = true;
			div_btn.btn_save.visible = true;  
		}
		else
		{// alert("4");
			cb_dept.Enable 	= false;
			ed_supman.Enable 	= true;
			cb_dept2.Enable 	= true;
			cal_suprdt.Enable 	= false;
			ed_pjt.Enable 		= false;
			btn_pjt.Enable      = false;
			cb_gbn.Enable 		= false;
			//cb_sup_cd.Enable 	= false;
			//ta_bigo.Enable      = false;
			ta_bigo.Readonly    = true;
			ta_bigo2.Readonly    = true;
			ta_bigo3.Enable     = true;

			//ed_supman.Enable    = true;
			//cal_supdt.Enable    = true;

			//ed_prwrk.Enable     = false;
		//	cb_gbn2.Enable 		= false;

			Edit2.Enable        = false;
			Edit0.Enable		= false;
			//grd_list.Enable     = false;

			div_btn.btn_query.visible = false;
			div_btn.btn_delete.visible = false;
			div_btn.btn_save.visible = true;
		}


	}
	else if(chk=="N")
	{ //alert("5");

		cb_dept.Enable 	= true;
		ed_supman.Enable 	= true;
		cb_dept2.Enable 	= true;
		cal_suprdt.Enable 	= true;
		ed_pjt.Enable 		= false;
		btn_pjt.Enable      = true;
		cb_gbn.Enable 		= true;
	//	cb_sup_cd.Enable 	= true;
		//ta_bigo.Enable      = true;
		ta_bigo.Readonly    = false;
		ta_bigo2.Readonly    = false;
		ta_bigo3.Enable     = false;

		//ed_supman.Enable    = false;
		//cal_supdt.Enable    = false;

		//ed_prwrk.Enable     = true;
		//cb_gbn2.Enable 		= true;

		Edit2.Enable        = false;
		Edit0.Enable		= false;
	//	grd_list.Enable     = true;


		div_btn.btn_query.visible = true;
		div_btn.btn_delete.visible = true;
		div_btn.btn_save.visible = true;     
	}
}	



