/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {	
//alert(PER_CHK);	

	if(PER_CHK == "C")
	{		  

	   // alert("a");
		if(gfn_isNull(cb_dept, true, Static9.Text)) return;
		if(gfn_isNull(cal_suprdt, true, Static27.Text)) return;
		if(gfn_isNull(ed_pjt, true, Static1.Text)) return;		
		if(gfn_isNull(ed_posid, true, Static11.Text)) return;		
		if(gfn_isNull(cb_gbn, true, Static7.Text)) return;


		tit_clearActionInfo();		   
		tit_addSearchActionInfo("ps12:PS1204003B_S8");         		    

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_master=ds_master"
			, ""
			, "fn_afterQuery"
			, ""
			, ""
			, true);


	}
    else if(PER_CHK == "N")
    {
     //   alert("b"); 
		if(gfn_isNull(ed_pjt, true, Static1.Text)) return;		
		if(gfn_isNull(ed_posid, true, Static11.Text)) return;		

		tit_clearActionInfo();		  		     
		tit_addSearchActionInfo("ps12:PS1204001B_S4");         		      
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_master=ds_master"
			, ""
			, "fn_afterQuery"
			, ""
			, ""
			, true);	


    }



}    


