/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{
	if(gfn_isNull(cb_dept, true, Static9.Text)) return false; 
	if(gfn_isNull(cal_suprdt, true, Static27.Text)) return false;
	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false;
	if(gfn_isNull(ed_posid, true, Static11.Text)) return false;
	if(gfn_isNull(cb_gbn, true, Static7.Text)) return false;
	if(gfn_isNull(cb_dept2, true, Static8.Text)) return false;
	if(gfn_isNull(ed_supman, true, Static12.Text)) return false;

	//if(gfn_isNull(cb_gbn2, true, Static6.Text)) return false;


	//if(gfn_isNull(cb_sup_cd, true, Static8.Text)) return false;
	//if(gfn_isNull(ed_supman, true, Static12.Text)) return false;




    ds_chk.clearData();
	if(PER_CHK == "N")
	{

		tit_clearActionInfo();		
		tit_addSearchActionInfo("ps12:PS1204003B_S1"); 
		tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_chk=ds_chk"
				, ""
				, ""//"fn_afterQuery"
				, ""
				, ""
				, true);


	}

	if(ds_chk.GetRowCount() > 0)	
	{
		gfn_showMsg("CW00137");
		return;
	}


	ds_save.ClearData();	
	ds_save.AddRow();	
	ds_save.SetColumn(0, "BIGO", ds_master.GetColumn(0, "BIGO"));
	ds_save.SetColumn(0, "BIGO2", ds_master.GetColumn(0, "BIGO2"));
	ds_save.SetColumn(0, "SUP_DEPT", ds_master.GetColumn(0, "SUP_DEPT"));
	ds_save.SetColumn(0, "SUP_MAN", ds_master.GetColumn(0, "SUP_MAN"));
	//ds_save.SetColumn(0, "SUP_TEL", ds_TEL.GetColumn(0, "TEL"));
	//ds_save.SetColumn(0, "PROWRK", ds_master.GetColumn(0, "PROWRK"));

//	ds_save.SetColumn(0, "SUP_CD", ds_master.GetColumn(0, "SUP_CD"));
	//ds_save.SetColumn(0, "SUP_DT", ds_master.GetColumn(0, "SUP_DT"));
	//ds_save.SetColumn(0, "SUP_MAN", ds_master.GetColumn(0, "SUP_MAN"));

	ds_save.SetColumn(0, "PSPID", ds_cond.GetColumn(0, "PSPID"));
	ds_save.SetColumn(0, "POSID", ds_cond.GetColumn(0, "POSID"));	
	ds_save.SetColumn(0, "GBN", ds_cond.GetColumn(0, "GBN"));
	ds_save.SetColumn(0, "DEPT", ds_cond.GetColumn(0, "DEPT"));
	ds_save.SetColumn(0, "SUPR_DT", ds_cond.GetColumn(0, "SUPR_DT"));
//	ds_master.SetColumn(0, "SUP_TEL", ds_TEL.GetColumn(0, "TEL"));


	//ds_save.SetColumn(0, "NAMET", 			ds_master.GetColumn(0, "NAMET"));



	tit_clearActionInfo();		
	tit_addSelectExActionInfo("ps12:PS1204003B_S2", "ps12:PS1204003B_I1", "ps12:PS1204003B_U1");
	tit_callService(
		""
		, ""
		, "ds_save=ds_save"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);


}



/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		ufn_enable("C");
		PER_CHK = 'C';  //저장시 변경으로 들어감
		gfn_showMsg("CI00003");	
	//    fn_query();
//		parent.fn_query();
        fn_supman();
	}
}


/********************************************************************************
* 기      능   : 접수자 연락처 연동
********************************************************************************/
function fn_supman(errCode, errMsg) {

	tit_clearActionInfo();		  		     
	tit_addSearchActionInfo("ps12:PS1204003B_S10");         		      
	tit_callService(
		""
		, ""
		, "ds_save=ds_save"
		, "ds_man=ds_man"
		, ""
		, "fn_sms"
		, ""
		, ""
		, true);
}



/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() 
{

	if(gfn_isNull(cb_dept, true, Static9.Text)) return false; 
	if(gfn_isNull(cal_suprdt, true, Static27.Text)) return false;
	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false;
	if(gfn_isNull(ed_posid, true, Static11.Text)) return false;
	if(gfn_isNull(cb_gbn, true, Static7.Text)) return false;

	if(PER_CHK == "C")
	{
		if (!gfn_showConfirm("CI00015","기술지원 요청서")) 
		{
			return;
		}	


		tit_clearActionInfo();	
		//tit_addSingleActionInfo("bs04:BS0409002B_D2");
		tit_addSingleActionInfo("ps12:PS1204003B_D1");
		tit_callService(
			""
			, ""        
			, "ds_cond=ds_cond"
			, ""
			, ""
			, "fn_afterDelete"
			, ""
			, ""
			,true  // ""
			);	
      }

	//fn_reset("D");

}




/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00004");		
		parent.fn_query();   
		//fn_query();
		fn_close();
	}
}


/********************************************************************************
* 기      능   : SMS 보내기 / 이메일 보내기
********************************************************************************/
function fn_sms()
{

	//if(  length(trim(Edit0.Text)) == 0 )
	//{
		//gfn_showAlert("CW00113", '팀장전화번호');
		//return;
//	}

	if ( length(trim(ed_supman.Text)) == 0 || length(trim(cal_suprdt.Value)) == 0 )
	{
		//gfn_showAlert("CW00221", 'SMS발송시 접수자, 지원예정일');
		//return;
	}

	var lv_msg = "";
    var user;
    var perno;
	var chkmsg;

	lv_msg = '' + ed_posid.Text + '호기(' + ed_bstnk.Text + ')의 기술지원을 ' + cal_suprdt.Value + '일에 요청드립니다.' + 
	         '신청부서 : ' + cb_dept.Text ;
	user =  'helco' + G_USER_ID + '@hdel.co.kr';        
   	perno = 'helco' + substr(ds_man.GetColumn(0, "PSNO"),1,7) + '@hdel.co.kr';
    chkmsg = '' + ed_bstnk.Text +'현장의 기술지원 요청';


	gfn_initDs(ds_sms, true);
	ds_sms.SetColumn(0,"TRAN_ETC1", "PS1204003B");
	//ds_sms.SetColumn(0,"TRAN_TYPE", "1");
	//ds_sms.SetColumn(0,"TRAN_STATUS", "1");
	ds_sms.SetColumn(0,"TRAN_TYPE", "0");
	ds_sms.SetColumn(0,"TRAN_STATUS", "0");
	ds_sms.SetColumn(0,"TRAN_CALLBACK", "1577-0603");
	ds_sms.SetColumn(0,"TRAN_MSG", lv_msg);
	//ds_sms.SetColumn(0,"TRAN_PHONE", "01047145711");
    ds_sms.SetColumn(0,"TRAN_PHONE", ds_man.GetColumn(0,"TELH"));
   	ds_man.SetColumn(0, "MESGDESC", lv_msg);
   	ds_man.SetColumn(0, "USER", user);
   	ds_man.SetColumn(0, "PERNO",  perno);
   	ds_man.SetColumn(0, "MESGTITL", chkmsg);


//trace(	ds_sms.SaveXML());
//alert("xxx");	
	tit_clearActionInfo();
	tit_addMultiActionInfo("ps07:PS0704001A_I2");
	tit_callService(
		"PDA_ACT"
		, ""
		, "ds_sms=ds_sms"
		, ""
		, ""
		, "fn_smsSend" //""
		, ""
		, ""
		, true);




}



/********************************************************************************
* 기      능   : sms 메시지 Callback  
********************************************************************************/
function fn_smsSend(errCode, errMsg) 
{

	if ( errCode != 0 ) 
	{
		global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
		global.FRM_BOTTOM.st_msg.color = "yellow";       
		global.FRM_BOTTOM.st_msg.value = "SMS발송을 실패하였습니다.";
		return;
	} 
    else 
    {
		global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
	    global.FRM_BOTTOM.st_msg.color = "yellow";       
		global.FRM_BOTTOM.st_msg.value = "SMS발송이 되었습니다.";
	}

	fn_mail();
}



/********************************************************************************
* 기      능   : mail
********************************************************************************/
function fn_mail()
{
tit_callService(
			"COMM_ACT"
			, "sendmail"
			, ""
			, ""
			//, "USER_JUMIN=" + quote(med_jumin1.value+med_jumin2.value)
			//	+ 
			," SEND_USER=" + quote( ds_man.GetColumn(0,"USER") )
				+ " RECE_USER=" + quote( ds_man.GetColumn(0,"PERNO") )
				+ " MESGTITL=" + quote( ds_man.GetColumn(0,"MESGTITL") )
				+ " MESGBODY=" + quote( ds_man.GetColumn(0,"MESGDESC") )
			, ""  //"fn_afterQuery"
			, false
			, true);			

}

