/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{
	if(gfn_isNull(cb_dept, true, Static20.Text)) return;
	if(gfn_isNull(cal_suprdt, true, Static27.Text)) return;
	if(gfn_isNull(ed_pjt, true, Static1.Text)) return;		
	if(gfn_isNull(ed_posid, true, Static11.Text)) return;		
	if(gfn_isNull(cb_gbn, true, Static7.Text)) return;
	if(gfn_isNull(ed_supman2, true, Static12.Text)) return;
	if(gfn_isNull(ed_supman, true, Static16.Text)) return;
	if(gfn_isNull(cal_supdt, true, Static14.Text)) return;			
	if(gfn_isNull(cb_finish, true, Static15.Text)) return;
	if(gfn_isNull(ta_bigo, true, Static19.Text)) return;
	if(gfn_isNull(ta_bigo2, true, Static2.Text)) return;			
	if(gfn_isNull(ta_bigo3, true, Static6.Text)) return;	
	if(gfn_isNull(cb_rusult, true, Static5.Text)) return;	
	if(gfn_isNull(ta_bigo4, true, Static23.Text)) return;	
	//if(gfn_isNull(cal_supdt2, true, Static16.Text)) return;


/*
    ds_chk.clearData();
	if(PER_CHK == "N")
	{

		tit_clearActionInfo();		
		tit_addSearchActionInfo("ps12:PS1204001B_S5"); 
		tit_callService(
				""
				, ""
				, "ds_cond=ds_cond"
				, "ds_chk=ds_chk"
				, ""
				, ""//"fn_afterQuery"
				, ""
				, ""
				, true);


	}

	if(ds_chk.GetRowCount() > 0)	
	{
		gfn_showMsg("CW00137");
		return;
	}
*/			

	ds_save.ClearData();	
	ds_save.AddRow();	
	ds_save.SetColumn(0, "BIGO", ds_master.GetColumn(0, "BIGO"));
	ds_save.SetColumn(0, "BIGO2", ds_master.GetColumn(0, "BIGO2"));
	ds_save.SetColumn(0, "BIGO3", ds_master.GetColumn(0, "BIGO3"));
	ds_save.SetColumn(0, "BIGO4", ds_master.GetColumn(0, "BIGO4"));
	ds_save.SetColumn(0, "BACKSAYOO", ds_master.GetColumn(0, "BACKSAYOO"));

	ds_save.SetColumn(0, "SENDBACK", ds_master.GetColumn(0, "SENDBACK"));
	ds_save.SetColumn(0, "SUP_CD", ds_master.GetColumn(0, "SUP_CD"));
	ds_save.SetColumn(0, "SUP_MAN", ds_master.GetColumn(0, "SUP_MAN"));
	ds_save.SetColumn(0, "SUP_DT", ds_master.GetColumn(0, "SUP_DT"));
	ds_save.SetColumn(0, "SUP_DEPT", ds_master.GetColumn(0, "SUP_DEPT"));
	ds_save.SetColumn(0, "FINISH", ds_master.GetColumn(0, "FINISH"));
	ds_save.SetColumn(0, "SUP_FN", ds_master.GetColumn(0, "SUP_FN"));
    ds_save.SetColumn(0, "SUP_DEPT3", ds_master.GetColumn(0, "SUP_DEPT3"));
	ds_save.SetColumn(0, "SUP_MAN3", ds_master.GetColumn(0, "SUP_MAN3"));
    ds_save.SetColumn(0, "SUP_MAN2", ds_master.GetColumn(0, "SUP_MAN2"));

/*	var lv_mh = 0.0;
	if (length(trim(ds_master.GetColumn(0, "MH"))) == 0)
	{
		lv_mh = 0.0;
	}
	else
	{
		lv_mh = ds_master.GetColumn(0, "MH");
	}
	//ds_save.SetColumn(0, "MH", ds_master.GetColumn(0, "MH"));
	ds_save.SetColumn(0, "MH", lv_mh);

*/	
	ds_save.SetColumn(0, "PSPID", ds_cond.GetColumn(0, "PSPID"));
	ds_save.SetColumn(0, "POSID", ds_cond.GetColumn(0, "POSID"));	
	ds_save.SetColumn(0, "GBN", ds_cond.GetColumn(0, "GBN"));
	ds_save.SetColumn(0, "DEPT", ds_cond.GetColumn(0, "DEPT"));
	ds_save.SetColumn(0, "SUPR_DT", ds_cond.GetColumn(0, "SUPR_DT"));






	tit_clearActionInfo();		
	//tit_addSelectExActionInfo("ps12:PS1204001B_S6", "ps12:PS1204001B_I1", "ps12:PS1204001B_U1");
	tit_addSelectExActionInfo("ps12:PS1204004B_S2", "ps12:PS1204004C_I1", "ps12:PS1204004C_U1");
	tit_callService(
		""
		, ""
		, "ds_save=ds_save"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);





}



/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		//ufn_enable("C");
		//PER_CHK = 'C';  //저장시 변경으로 들어감
		gfn_showMsg("CI00003");		
		PER_CHK = 'B'; //저장후 기술지원보고서로 변경
		//parent.fn_query();
		fn_supman();
	}
}


