********************************************************************************/	
function ufn_enable(chk)
{

 if(PRE_MAX == "X")
 {
//alert(PRE_GBN);
 if(PRE_GBN == 'Y')	
	{

			cal_suprdt.Enable 	= true;
			ed_pjt.Enable 		= false;
			ed_tel.Enable 		= false;
			//btn_pjt.Enable      = false;
			//ta_bigo.Enable      = false;
			ta_bigo.Enable    = true;
			ta_bigo2.Enable    = true;
			cb_gbn.Enable      = true;
			rd_gbn.Enable        = false;
  		    rd_gbn2.Enable		= false;
			div_btn.btn_query.visible = false;
			//div_btn.btn_delete.visible = flase;
			div_btn.btn_save.visible = true;
			div_btn.btn_trans.visible = true;
			div_btn.btn_print.visible = true;
			cal_plan.Enable 	= true;

			Calendar0.Enable 	= true;


	}

 else 
  {
 			cal_suprdt.Enable 	= true;
			ed_pjt.Enable 		= false;
			ed_tel.Enable 		= false;
			btn_pjt.Enable      = false;
			//ta_bigo.Enable      = false;
			ta_bigo.Enable    = true;
			ta_bigo2.Enable    = false;
			cb_gbn.Enable      = true;
			rd_gbn.Enable        = false;
  		    rd_gbn2.Enable		= false;
			div_btn.btn_query.visible = false;
			div_btn.btn_trans.visible = false;
			div_btn.btn_save.visible = false;
			div_btn.btn_print.visible = true;
			cal_plan.Enable 	= false;

			Calendar0.Enable 	=  true;
   }
}
else
 {
   div_btn.btn_finish.visible = false;
   div_btn.btn_save.visible = false;
   div_btn.btn_trans.visible = false;
 }
}	 

