/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{
	if(gfn_isNull(cal_suprdt, true, Static27.Text)) return false;
	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false;
	if(gfn_isNull(edt_lifnr, true, Static10.Text)) return false;
	if(gfn_isNull(cb_tmcd, true, Static19.Text)) return false;
	if(gfn_isNull(cb_gbn, true, Static4.Text)) return false;	
	if(gfn_isNull(rd_gbn, true, Static7.Text)) return false;	
	if(gfn_isNull(rd_gbn2, true, Static6.Text)) return false;
	if(gfn_isNull(ta_bigo, true, Static5.Text)) return false;
	if(gfn_isNull(ta_bigo2, true, Static9.Text)) return false;
	if(gfn_isNull(cal_plan, true, Static10.Text)) return false;

    // ds_master.SetColumn(0,"SEQNO", ds_master.GetColumn(0,"SEQNO"));
    // ds_master.SetColumn(0,"SUPR_DT", ds_cond.GetColumn(0,"SUPR_DT"));


	tit_clearActionInfo();		
	if(ds_master.GetColumn(0,"SUPR_DT2") == '' || ds_master.GetColumn(0,"SUPR_DT2") == '00000000')
	{ 
	  if( ds_master.GetColumn(0,"SUPR_DT") == '' || ds_master.GetColumn(0,"SUPR_DT") == '00000000')
	  {
	    tit_addSearchActionInfo("ps12:PS1204006B_U1");
      }
      else{
	    tit_addSearchActionInfo("ps12:PS1204006B_D1");
 	    tit_addSearchActionInfo("ps12:PS1204006B_I1");
 	  }
 	}
 	else{
 	 tit_addSelectExActionInfo("ps12:PS1204006B_S1", "ps12:PS1204006B_I1", "ps12:PS1204006B_U1");
 	}
//	tit_addSelectExActionInfo("ps12:PS1204006B_S1", "ps12:PS1204006B_I1", "ps12:PS1204006B_U1");
	//tit_addSelectExActionInfo("ps12:PS1204006B_S1", "", "");
	tit_callService(
		""
		, ""
		, "ds_master=ds_master"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);	
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		//ufn_enable("C");
		//PER_CHK = 'C';  //저장시 변경으로 들어감
		PRE_GBN = 'Y' ;
		gfn_showMsg("CI00003");		
		parent.fn_query();
		fn_close();
	}
}

