function fn_trans() 
{
       // ds_master.SetColumn(0,"SUPR_DT", ds_cond.GetColumn(0,"SUPR_DT"));

  		tit_clearActionInfo();	
		//tit_addSelectExActionInfo("ps12:PS1204006B_S3", "ps12:PS1204006B_I2", "ps12:PS1204006B_U2");
