		}
		tit_addSearchActionInfo("ps12:PS1204006B_I2");	
         tit_callService(
			""
			, ""
			, "ds_master=ds_master"
			, ""
			, ""
			, "fn_aftertrans"
			, "" //true
			, ""
			, true);	


}


/********************************************************************************
