		gfn_showMsg("CE00001");
	} 
	else 
	{
		ufn_enable("C");
		//PER_CHK = 'C';  //저장시 변경으로 들어감
		gfn_showMsg("CI00003");	
		parent.fn_query();
		fn_close();
		//fn_afterTrans();		

    }

}
