/********************************************************************************
* 기      능   : 완료  
********************************************************************************/
function fn_finish() 
{
	//if(gfn_isNull(Calendar0, true, Static18.Text)) return false;
	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false;
	if(gfn_isNull(cb_gbn, true, Static4.Text)) return false;	
	if(gfn_isNull(rd_gbn, true, Static7.Text)) return false;	
	if(gfn_isNull(rd_gbn2, true, Static6.Text)) return false;
	if(gfn_isNull(ta_bigo, true, Static5.Text)) return false;
	if(gfn_isNull(ta_bigo2, true, Static9.Text)) return false;
   // if(gfn_isNull(ta_bigo3, true, Static12.Text)) return false;
    if(gfn_isNull(cal_plan, true, Static14.Text)) return false;
    if(gfn_isNull(cal_finsh, true, Static10.Text)) return false;
   // cal_finsh

    // ds_master.SetColumn(0,"SEQNO", ds_master.GetColumn(0,"SEQNO"));
    // ds_master.SetColumn(0,"SUPR_DT", ds_cond.GetColumn(0,"SUPR_DT"));
	tit_clearActionInfo();		
	if( ds_master.GetColumn(0,"SUPR_DT2") == '' || ds_master.GetColumn(0,"SUPR_DT2") == '00000000'){		
//     	tit_addMultiActionInfo("ps12:PS1204006C_U1");
        tit_addMultiActionInfo("ps12:PS1204006C_D1");
	    tit_addMultiActionInfo("ps12:PS1204006C_I1");}
	 else {
     	 if(ds_master.GetColumn(0,"SUPR_DT") == ''|| ds_master.GetColumn(0,"SUPR_DT") == '00000000' )
	     {
	       tit_addMultiActionInfo("ps12:PS1204006C_U2");
	     }else{
	   	   tit_addSelectExActionInfo("ps12:PS1204006C_S3", "ps12:PS1204006C_I1", "ps12:PS1204006C_U2"); 
	      }
	   }
	tit_addMultiActionInfo("ps12:PS1204006C_U3"); 
	tit_callService(
		""
		, ""
		, "ds_master=ds_master"
		, ""
		, ""
		, "fn_Sendsms"
		, ""
		, ""
		, true  // ""

		);	
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		ufn_enable("C");
		//PER_CHK = 'C';  //저장시 변경으로 들어감
		gfn_showMsg("CI00003");

   	if(ds_master.GetColumn(0,"STEL") <> '')
	{
	  TELL = "";
	  TELL = ds_master.GetColumn(0,"STEL");
	  fn_sms();
	}	
	if(ds_master.GetColumn(0,"JTELL") <> '')
	{
	  TELL = "";
	  TELL = ds_master.GetColumn(0,"JTELL");
	  fn_sms();
	}
	if(ds_master.GetColumn(0,"JTELL2") <> '')
	{
	  TELL = "";
	  TELL = ds_master.GetColumn(0,"JTELL2");
	  fn_sms();
	}
	if(ds_master.GetColumn(0,"CELLPT") <> '')
	{
	  TELL = "";
	  TELL = ds_master.GetColumn(0,"CELLPT");
 	  fn_sms();
	}

     parent.fn_query();
	 fn_close();
   }

}
/********************************************************************************
* 기      능   : SMS 보내기
********************************************************************************/
function fn_Sendsms()
{

	if(length(ds_master.GetColumn(0, "CELLP")) == 0 )
	{
		gfn_showAlert("CW00113", '접수자전화번호');
		return;
	}

	var lv_msg = "";
	lv_msg = '귀하가 요청하신 ' + ed_bstnk.Text + '(' + ed_pjt.Text + ')의 민원이 ' + 
	         cal_finsh.Value + ' 일에 처리 완료되었습니다.';
	gfn_initDs(ds_sms, true);
	ds_sms.SetColumn(0,"TRAN_ETC1", "PS1204006C");
	//ds_sms.SetColumn(0,"TRAN_TYPE", "1");
	//ds_sms.SetColumn(0,"TRAN_STATUS", "1");
	ds_sms.SetColumn(0,"TRAN_TYPE", "0");
	ds_sms.SetColumn(0,"TRAN_STATUS", "0");
	ds_sms.SetColumn(0,"TRAN_CALLBACK", "1577-0603");
	ds_sms.SetColumn(0,"TRAN_MSG", lv_msg);
    ds_sms.SetColumn(0,"TRAN_PHONE", ed_tel.Text);


//trace(	ds_sms.SaveXML());
//alert("xxx");	
	tit_clearActionInfo();
	tit_addMultiActionInfo("ps07:PS0704001A_I2");
	tit_callService(
		"PDA_ACT"
		, ""
		, "ds_sms=ds_sms"
		, ""
		, ""
		, "fn_smsSendAfter" //""
		, ""
		, ""
		, true);



}

/********************************************************************************
* 기      능   : sms 메시지 Callback  
********************************************************************************/
function fn_smsSendAfter(errCode, errMsg) 
{
	if ( errCode != 0 ) 
	{
		global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
		global.FRM_BOTTOM.st_msg.color = "yellow";       
		global.FRM_BOTTOM.st_msg.value = "SMS발송을 실패하였습니다.";
		return;
	} 
    else 
    {
		global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
		global.FRM_BOTTOM.st_msg.color = "yellow";       
		global.FRM_BOTTOM.st_msg.value = "SMS발송이 되었습니다.";
	}

		parent.fn_query();
		fn_close();

}

