********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() 
{
	//if(gfn_isNull(Calendar0, true, Static18.Text)) return false;
	if(gfn_isNull(ed_pjt, true, Static1.Text)) return false;
	if(gfn_isNull(cb_gbn, true, Static4.Text)) return false;	
	if(gfn_isNull(rd_gbn, true, Static7.Text)) return false;	
	if(gfn_isNull(rd_gbn2, true, Static6.Text)) return false;
	if(gfn_isNull(ta_bigo, true, Static5.Text)) return false;
	if(gfn_isNull(ta_bigo2, true, Static9.Text)) return false;
	if(gfn_isNull(cal_plan, true, Static10.Text)) return false;

    // ds_master.SetColumn(0,"SEQNO", ds_master.GetColumn(0,"SEQNO"));
//     ds_master.SetColumn(0,"SUPR_DT", ds_cond.GetColumn(0,"SUPR_DT"));

	tit_clearActionInfo();		


	if(ds_master.GetColumn(0,"SUPR_DT2") == '' || ds_master.GetColumn(0,"SUPR_DT2") == '00000000')
	{
	  if( ds_master.GetColumn(0,"SUPR_DT") == '' || ds_master.GetColumn(0,"SUPR_DT") == '00000000')
	  {
	    tit_addSearchActionInfo("ps12:PS1204006C_U2");
      }
      else{
	    tit_addMultiActionInfo("ps12:PS1204006C_D1");
	    tit_addMultiActionInfo("ps12:PS1204006C_I1");
	  //   tit_addMultiActionInfo("ps12:PS1204006C_U2");
	  }
	}
	else{
	   if(ds_master.GetColumn(0,"SUPR_DT") == ''|| ds_master.GetColumn(0,"SUPR_DT") == '00000000' )
	   {
	       tit_addMultiActionInfo("ps12:PS1204006C_U2");
	   }else{
	   	   tit_addSelectExActionInfo("ps12:PS1204006C_S3", "ps12:PS1204006C_I1", "ps12:PS1204006C_U2");
	   }
	}
	tit_callService(
		""
		, ""
		, "ds_master=ds_master"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);	
}
