/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {	

	if(PER_CHK == "C") //입력분
	{		  
	//	if(gfn_isNull(cb_lifnr, true, Static9.Text)) return;
		//if(gfn_isNull(cal_suprdt, true, Static27.Text)) return;
		//if(gfn_isNull(ed_pjt, true, Static1.Text)) return;			
		//if(gfn_isNull(ed_seq, true, Static8.Text)) return;			


		tit_clearActionInfo();		   
		tit_addSearchActionInfo("ps12:PS1204006D_S2");         		    

		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_master=ds_master"
			, ""
			, "fn_afterQuery"
			, ""
			, ""
			, true);


	}
    else if(PER_CHK == "N") // 미입력 
    {

		if(gfn_isNull(ed_pjt, true, Static1.Text)) return;	

		tit_clearActionInfo();		 
		tit_addSearchActionInfo("ps12:PS1204006D_S3"); 		      
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_master=ds_master"
			, ""
			, "fn_afterQuery"
			, ""
			, ""
			, true);	

	}

}    
