/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		//ufn_enable("C");
	     PER_CHK = 'C';  //저장시 변경으로 들어감
	     PRE_STT = 'G';
	//	gfn_showMsg("CI00003");	
		parent.fn_query();    
	//	fn_close();
		//fn_afterTrans();		

    }

  // fn_mail();

}
