function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);
	//gfn_initDs(ds_cond, true);

	user_grp = gfn_getUserGroup();

	Radio1.Index = 0;	

	//btn_save.visible = false;

	//user_grp = '10';
	//사진 업로드 필수여부 체크
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps12:PS1205001A_S3");	                       	
	tit_callService(
        ""
        , ""
        , ""
        , "ds_confirm=ds_confirm"
        , ""
        , ""
        , ""
        , ""
        , true);
    lv_necess_ck = ds_confirm.GetColumn(0, "NECESS_CK");


	if(Radio1.Value == "1")
    {
		if(lv_necess_ck == "1")
		{//alert("a");
			grd_list.Format = "EL";
		}
		else
		{//alert("b");
			grd_list.Format = "EL2";
		}
	}
	else if(Radio1.Value == "2")
	{
		if(lv_necess_ck == "1")
		{//alert("a");
			grd_list.Format = "ES";
		}
		else
		{//alert("B");
			grd_list.Format = "ES2";
		}
	}
	else if(Radio1.Value == "3")
	{
		if(lv_necess_ck == "1")
		{
			grd_list.Format = "ES";
		}
		else
		{
			grd_list.Format = "ES2";
		}
	}

	else if(Radio1.Value == "4")
	{
	  if(lv_necess_ck == "1")
		{
			grd_list.Format = "JUCHA";
		}
		else
		{
			grd_list.Format = "JUCHA2";
		}
	}

	ed_pjt.SetFocus();


	if( user_grp == '10' )
	{		
	    grd_list.Editable = false;
		btn_save.enable = false;  
	}
	else
	{		
		grd_list.Editable = true;
		btn_save.enable = true;   
	}


}


