/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() 
{   
    // 필수체크	
	if(gfn_isNull(ed_pjt, true, Static0.value))
		return;

	if(user_grp == '10') 
	{
		ds_cond.SetColumn(0,"GBN","10");

		//=협력업체는 계약형태 및 계약완료여부 보여지지 않게 수정=//
		Edit9.Visible = false;
		Edit5.Visible = false;
		//========================================================//
	} 
	else if(user_grp == '30' || user_grp == '40') 
	{
		ds_cond.SetColumn(0,"GBN","");
	}

	//E/L, E/S 구분
	if(Radio1.Value == "1")
    {
		ds_cond.SetColumn(0,"ZZGUBUN","1");

		if(lv_necess_ck == "1")
		{
			grd_list.Format = "EL";
		}
		else
		{
			grd_list.Format = "EL2";
		}

		//grd_list.Format = "EL";
	}
	else if(Radio1.Value == "2")
	{
		ds_cond.SetColumn(0,"ZZGUBUN","2");
		if(lv_necess_ck == "1")
		{
			grd_list.Format = "ES";
		}
		else
		{
			grd_list.Format = "ES2";
		}
		//grd_list.Format = "ES"

	}
	else if(Radio1.Value == "3")
	{
		ds_cond.SetColumn(0,"ZZGUBUN","3");
		if(lv_necess_ck == "1")
		{
			grd_list.Format = "ES";
		}
		else
		{
			grd_list.Format = "ES2";
		}
		//grd_list.Format = "ES"

	}
    else if(Radio1.Value == "4")
    {
        ds_cond.SetColumn(0,"ZZGUBUN","4");
		if(lv_necess_ck == "1")
		{
			grd_list.Format = "JUCHA";
		}
		else
		{
			grd_list.Format = "JUCHA2";
		}
    }


	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps12:PS1205002A_S2");
	tit_addSearchActionInfo("ps12:PS1205002A_S1");	
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_head=ds_head ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , ""
		, ""
        , true);    


}

