/********************************************************************************
* 기      능   : MR상부비계
********************************************************************************/
function fn_print1() 
{
	var path = '';

    //==============================================
	if(chk == 'EL1')
	{
		ds_cond3.SetColumn(0, "STEP", 'STEP1' );
	}
	else if(chk == 'EL2')
	{
		ds_cond3.SetColumn(0, "STEP", 'STEP2' );
	}
	else
	{
		ds_cond3.SetColumn(0, "STEP", 'STEP3' );
	}
    //===============================================


	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps12:PS1205002A_S4");	
	tit_callService(
          ""
        , ""
        , "ds_cond3=ds_cond3"
        , "ds_print=ds_print"
        , ""
        , ""
        , ""
		, ""
        , true);   



	if( length(ds_print.GetColumn(0, "IMAGE1")) != 0)
	{  
//alert('1');
//alert(path);	
		path = '';
		path = tit_getServerDir("QM", ds_print.GetColumn(0, "IMAGE1") ) ;
		//path = tit_getServerDir("QM", '20130610_132316_H2_100015');

		ds_print.SetColumn(0,"IMAGE1", path);
	}

	if( length(ds_print.GetColumn(0, "IMAGE2")) != 0)
	{
//alert('2');
//alert(path);
		path = '';
		path = tit_getServerDir("QM", ds_print.GetColumn(0, "IMAGE2") ) ;
		ds_print.SetColumn(0,"IMAGE2", path);
	}

    if( length(ds_print.GetColumn(0, "IMAGE3")) != 0)
	{
//alert('3');
//alert(path);	
		path = '';
		path = tit_getServerDir("QM", ds_print.GetColumn(0, "IMAGE3") ) ;
		ds_print.SetColumn(0,"IMAGE3", path);
	}


	if(chk == 'EL1') //1단계
	{
	   if(ds_print.GetColumn(0,"TYPE") == 'EL01' || ds_print.GetColumn(0,"TYPE") == 'EL04')
	   {

		 Dialog("PS12::PS1205002P.xml", "gbn='EL1'");
	   }
