   //if ( !gfn_checkFromToObj(cal_f_from, cal_f_to, true, Static2.value, 0, true)   ) 
	//{
	//	return;
	//}


	    tit_clearActionInfo();	
	 	tit_addSearchActionInfo("ps12:PS1206002A_S1");	
	 	tit_addSearchActionInfo("ps12:PS1206002A_S2");	
		tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list ds_head=ds_head"
        , ""
        , "fn_afterQuery"
        , ""
        , ""
        , true );    



}
