/********************************************************************************
* 기      능   :  점검자 문자 발송
********************************************************************************/
function btn_sms_OnClick(obj)
{
  var rows = 0;

// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

  for(var i=0;i<ds_list.GetRowCount();i++)
  {
    if(ds_list.GetColumn(i,"CHECK") == '1')
    {
      ds_list.SetColumn(i,"GBN",ds_cond.GetColumn(0,"GBN") );

      rows++;
    }
  }

  ds_head.SetColumn(0,"CNT", rows);
 // alert(rows);

	if (!gfn_showConfirm("CW10025","서비스 점검자에게 문자통보 하시겠습니까?")) 
	{
		return;
	}

   //주점검사 sms		
    if(length(ds_list.GetColumn(0,"JUJ_HP")) > 0 )
    {
      fn_sms();
    }
    //부 점검자  sms
   if(length(ds_list.GetColumn(0,"BUJ_HP")) > 0 )
    {
      fn_sms2();
    }


     ds_list.Filter("CHECK==1");
	 ds_list2.copyF(ds_list);
     ds_list.unFilter();

     tit_clearActionInfo();	
	 tit_addMultiActionInfo("ps12:PS1206002A_I1");	
     tit_callService(
			""
			, ""
			, "ds_list2=ds_list2"
			, ""
			, ""
			, "fn_afterSave"
			, "" //true
			, ""
			, true);	



  	 fn_query();

}
/********************************************************************************
* 기      능   :  주점검자 문자 발송
********************************************************************************/
function fn_sms()
{
//alert("a");
	var lv_msg = "";
	var tell = "";
//	var tell = "010-4714-5711";

	lv_msg = '[점검자 입회 통보]' + ed_pjt.Text + '(' + ed_zsite.Text + ') 보양철거 ' + ""+ " "
	          + ds_head.GetColumn(0,"CNT") + '대 '  + ""+ " "
	           + cal_f_from.Value + ' ~ '+ cal_f_to.Value +' 작업예정.'  + ""+ " "
	           + ds_cond.GetColumn(0,"LIFNR");
	//lv_msg = "TEST";
	var tell = "010-5062-6749"; 


	gfn_initDs(ds_sms, true);

	ds_sms.SetColumn(0,"TRAN_ETC1", "PS1206602A");
    ds_sms.SetColumn(0,"TRAN_TYPE", "0");
	ds_sms.SetColumn(0,"TRAN_STATUS", "0");
	ds_sms.SetColumn(0,"TRAN_CALLBACK", "1577-0603");
	ds_sms.SetColumn(0,"TRAN_MSG", lv_msg);
	ds_sms.SetColumn(0,"TRAN_PHONE",tell);	    

//trace(ds_sms.SaveXML()) ;

     	tit_clearActionInfo();
     	tit_addMultiActionInfo("ps07:PS0704010A_I1");

	    tit_callService(
	   	 "PDA_ACT"
	    , ""
		, "ds_sms=ds_sms"
		, ""
		, ""
		, ""
		, ""
		, ""
		, true);


}
/********************************************************************************
* 기      능   :  부점검자 문자 발송
********************************************************************************/
function fn_sms2()
{
//alert("b");

	var lv_msg = "";
	var tell = "";
//	var tell = "010-4714-5711";

	lv_msg = '[점검자 입회 통보]' + ed_pjt.Text + '(' + ed_zsite.Text + ') 보양철거 '  + " "
	          + ds_head.GetColumn(0,"CNT") + '대 '  + " "
	           + cal_f_from.Value + ' ~ '+ cal_f_to.Value +' 작업예정.'  + " "
	           + ds_cond.GetColumn(0,"LIFNR");
	//lv_msg = "TEST";
