	chk_so4.Value = '1';

	cal_f_fromm.value = addMonth(gfn_getCurrDate(), -6);
	cal_f_too.value = gfn_getLeapLastDay(gfn_getCurrDate());



	ds_cond.SetColumn(0,"USER_GRUP",userGroup);
	if (userGroup == "10")
	{
		ds_cond.SetColumn(0,"USER_CODE",G_VEND_CODE_E);
	}
	else if (userGroup == "20")
