		, ""
		, ""
		, true);


	cal_f_fromm.SETFOCUS();

	gfn_showMsg("CI00007");


}

/********************************************************************************
* 기      능   :  협력사 조회 Callback  
********************************************************************************/
function fn_afterQueryC(errCode, errMsg) {
	cb_zzlifnr.Index = 0;


	if ( userGroup == "10" )
	{
		cb_zzlifnr.Enable = false;
	}
	else if ( userGroup == "30" || userGroup == "40")
	{
//alert(ds_zzlifnr.SaveXML());						
		ds_zzlifnr.filter("LOEVM!='X'" );		
		gfn_insertEmptyRow( ds_zzlifnr, G_CODE_ALL );
		gfn_insertEmptyRow( ds_zzactss, G_CODE_ALL );
		cb_zzlifnr.value = "";
	}

}


/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) 
{

	//alert(userGroup);
	//alert(G_AREA_CODE_E);
	//alert(cb_zzactss.Index);
	//alert(ds_zzactss.SaveXML());	

	if(userGroup == "40")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = true;
	}
	else if(userGroup == "30")
	{
		ds_zzactss.Filter("CODE="+quote(G_AREA_CODE_E));
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}
	else if(userGroup == "10")
	{
		cb_zzactss.Index = 0;
		cb_zzactss.Enable = false;
	}

}


