/********************************************************************************
* 기      능   : 리스트의 타이틀 클릭  
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{

     if ( nCell == 0 ) {
		// 현재 처리할 format 기억
		gfn_selectGridCol("grd_list");
	} else {
		gfn_sortGrid(obj,nCell);
	}
}


function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if( nCell== 1) 
	{
       var arg  = "PER_FROM="+quote(cal_f_from.Value);
	       arg += " PER_TO="+quote(cal_f_to.Value);

	    gfn_openPopup("PS1207001B",true,arg);
	}

}

/********************************************************************************
* 기      능   : 부서별 사무소 코드 필터 
********************************************************************************/

function cb_dept_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var dept  = ds_cond.GetColumn(0,"DEPT");
    if(dept = '')
    ds_zzactss.UnFilter(); else ds_zzactss.filter("DEPART==" + quote( dept) );
    ds_cond.SetColumn(0,"ZZACTSS", '');
	//alert(dept);
}
/********************************************************************************
* 기      능   : 사무소 별 협력업체 필터 
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
     if(userGroup == "40" || userGroup == "30"){
     ds_cond.SetColumn(0,"ZZLIFNR",'');}

    if(ds_cond.GetColumn(0,"ZZACTSS") == '')
    {
      ds_zzlifnr.UnFilter();
    }
    else
    {
      var arr = ds_cond.GetColumn(0,"ZZACTSS");
	  ds_zzlifnr.Filter("ZZACTSS ==" +quote(arr));
    }
}
