function fn_afterQuery(errCode, errMsg) {
//alert(ed_pjt.text);

   //gfn_showMsg("CI00002",ds_list.RowCount()+"");
  //gfn_showSearchMsg(grd_list);
  tit_clearActionInfo();	

   if(ed_pjt.text ==  ''){
     tit_addSearchActionInfo("ps12:PS1208001A_S1");
     tit_addSearchActionInfo("ps12:PS1208001A_S6"); 
     }
   else{
     if(ds_cnt.GetColumn(0,"CNT") > 0)
       {
         tit_addSearchActionInfo("ps12:PS1208001A_S4");
         tit_addSearchActionInfo("ps12:PS1208001A_S6"); 
       }
     else
       {
          tit_addSearchActionInfo("ps12:PS1208001A_S3");
          tit_addSearchActionInfo("ps12:PS1208001A_S6"); 
       }
     }
      tit_callService(
      ""
    , ""
    , "ds_cond=ds_cond"
    , "ds_list2=ds_list2 ds_zztemno_grd_cd=ds_zztemno_grd_cd"
    , ""
    , ""//"fn_afterQuery"
    , true);    

     }


