function btn_add_OnClick(obj)
{

	/*if(length(trim(ds_head.GetColumn(0,"ZSITE_NM"))) <= 0)
	{

		gfn_showMsg("CW00054");
		return;
	}	
*/
	ds_list2.AddRow();			
	ds_list2.SetColumn(ds_list2.currow,"PSPID", ds_cond.GetColumn(0,"PSPID"));
    ds_list2.SetColumn(ds_list2.currow,"MAKE_MM", gfn_getCurrDate());
    ds_list2.SetColumn(ds_list2.currow,"ZSITE_NM", ds_list2.GetColumn(0,"ZSITE_NM"));
    ds_list2.SetColumn(ds_list2.currow,"CDATE",gfn_getCurrDate());

     ds_list2.fireevent = true;



}

/********************************************************************************
* 기      능   : (-)버튼 클릭시
********************************************************************************/
function btn_del_OnClick(obj)
{
	if(length(trim(ds_list2.GetColumn(0,"PSPID"))) <= 0)
	{
		gfn_showMsg("CW00054");
		return;
	}

if(ds_list2.GetColumn(ds_list2.row, "CHK") == 'X')
	{
		gfn_showAlert("CW00022","저장분은 삭제하실수 없습니다."
		);
		return;
	}



	ds_list2.DeleteRow(ds_list2.row);

}
