// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback 
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	gfn_showSearchMsg(grd_list, errCode, errMsg);
	for(var i = 0; i<ds_list.rowcount; i++)
	{
		if(length(ds_list.GetColumn(i,"LIFNR")) > 0)
		{
	      	var tmp_p1 = "0";
			var tmp_p2 = "0";
			var tmp_p3 = "0";
			var tmp_p4 = "0";
			var tmp_p5 = "0";
			var tmp_p6 = "0";
			var tmp_p7 = "0";
			var tmp_p8 = "0";
			var tmp_p9 = "0";
			var tmp_p10 = "0";
			var tmp_p11 = "0";

			tmp_p1 = ds_list.GetColumn(i, "CURQTY");
            tmp_p2 = ds_list.GetColumn(i, "CURQTY2");
            tmp_p3 = ds_list.GetColumn(i, "CURQTY3");
            tmp_p4 = ds_list.GetColumn(i, "IPQTY2"); 
            tmp_p5 = ds_list.GetColumn(i, "IPQTY3");
            tmp_p6 = ds_list.GetColumn(i, "IPQTY4");
            tmp_p7 = ds_list.GetColumn(i, "IPQTY5");
            tmp_p8 = ds_list.GetColumn(i, "BALJU");
            tmp_p9 = ds_list.GetColumn(i, "BALJU2");
            tmp_p10= ds_list.GetColumn(i, "VALUE");
            tmp_p11= ds_list.GetColumn(i, "VALUE2");



		    if(toNumber(tmp_p1) == 0)
         	{
	          ds_list.SetColumn(i, "CURQTY", "");
	        }
	     else{
              ds_list.SetColumn(i, "CURQTY", tmp_p1);	
	        }
	     if(toNumber(tmp_p2) == 0)
         	{
	          ds_list.SetColumn(i, "CURQTY2", "");
	        }
	     else{
              ds_list.SetColumn(i, "CURQTY2", tmp_p2);	
	        }
	     if(toNumber(tmp_p3) == 0)
         	{
	          ds_list.SetColumn(i, "CURQTY3", "");
	        }
	     else{
              ds_list.SetColumn(i, "CURQTY3", tmp_p3);	
	        }
	     if(toNumber(tmp_p4) == 0)
         	{
	          ds_list.SetColumn(i, "IPQTY2", "");
	        }
	     else{
              ds_list.SetColumn(i, "IPQTY2", tmp_p4);	
	        }
	     if(toNumber(tmp_p5) == 0)
         	{
	          ds_list.SetColumn(i, "IPQTY3", "");
	        }
	     else{
              ds_list.SetColumn(i, "IPQTY3", tmp_p5);
	        }
	     if(toNumber(tmp_p6) == 0)
         	{
	          ds_list.SetColumn(i, "IPQTY4", "");
	        }
	     else{
              ds_list.SetColumn(i, "IPQTY4", tmp_p6);	
	        }
	     if(toNumber(tmp_p7) == 0)
         	{
	          ds_list.SetColumn(i, "IPQTY5", "");
	        }
	     else{
              ds_list.SetColumn(i, "IPQTY5", tmp_p7);	
	        }   
	     if(toNumber(tmp_p8) == 0)
         	{
	          ds_list.SetColumn(i, "BALJU", "");
	        }
	     else{
              ds_list.SetColumn(i, "BALJU", tmp_p8);	
	        }   
	     if(toNumber(tmp_p9) == 0)
         	{
	          ds_list.SetColumn(i, "BALJU2", "");
	        }
	     else{
              ds_list.SetColumn(i, "BALJU2", tmp_p9);	
	        }   
	     if(toNumber(tmp_p10) == 0)
         	{
	          ds_list.SetColumn(i, "VALUE", "");
	        }
	     else{
              ds_list.SetColumn(i, "VALUE", tmp_p10);	
	        }  
	     if(toNumber(tmp_p11) == 0)
         	{
	          ds_list.SetColumn(i, "VALUE2", "");
	        }
	     else{
              ds_list.SetColumn(i, "VALUE2", tmp_p11);	
	        }  
		}
		}


}




