function fn_save() {

	if ( !gfn_isChangeDs(ds_list2, true, "") ) 
	{
		return;
	}
 /*   for(var i=0; i<ds_list2.GetRowCount(); i++)
    {
      ds_list2.SetColumn(i,"MAKE_MM",substr(gfn_getCurrDate(),0,6));
    }
  */
if(ds_list2.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	ds_list2.Filter("CHECK==1");
	ds_list3.copyF(ds_list2);
	ds_list2.unFilter();


      tit_clearActionInfo();	
		//tit_addSingleActionInfo("bs04:BS0409001B_D1");
		//tit_addSingleActionInfo("bs04:BS0410007B_D2");
		tit_addMultiActionInfo("ps12:PS1209001A_D1");
		tit_callService(
			""
			, ""        
			, "ds_list2=ds_list3"
			, ""
			, ""
			, ""
			, ""
			, ""
			,true  // ""
			);	


		if(ds_list2.GetRowCount() > 0)
		{

			tit_clearActionInfo();				
			//tit_addMultiActionInfo("bs04:BS0409001B_I2");
			tit_addMultiActionInfo("ps12:PS1209001A_I1");
			tit_callService(
				""
				, ""        
				, "ds_list2=ds_list3"
				, ""
				, ""
				, ""
				, ""
				, ""
				,true  // ""
				);	
		}

	/*tit_clearActionInfo();
	tit_addSelectExActionInfo("ps12:PS1208001A_S5", "ps12:PS1208001A_I1", "ps12:PS1208001A_U1");

	tit_callService(
		""
		, ""
		, "ds_list2=ds_list3"
		, ""
		, ""
		, ""//"fn_afterSave"
		, ""
		, ""
		, true
		) ;

	*/

		fn_query();

}


