function fn_query2(nRow) {
	ds_list2.ClearData();

	ds_cond2.ClearData();
	ds_cond2.AddRow();
	ds_cond2.setColumn(0, "LIFNR", ds_list.GetColumn(nRow,"LIFNR"));
	ds_cond2.setColumn(0, "TEMNO", ds_list.GetColumn(nRow,"TEMNO1"));
	ds_cond2.setColumn(0, "POSIT", ds_list.GetColumn(nRow,"POSIT"));
	ds_cond2.setColumn(0, "POSIT", ds_list.GetColumn(nRow,"POSIT"));
	ds_cond2.setColumn(0, "GBN", ds_list.GetColumn(nRow,"GBN"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps13:PS1301001A_S2", null, true);

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond2"
        , "ds_list2=ds_list"
        , ""
        , "fn_afterQuery2"
        , true);
}
/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	if ( !gfn_isChangeDs(ds_list2, true, "")){
		return;
	}

	ds_list3.ClearData(); //ZPST0132테이블에 변경이력을 저장한다.
	for(i=0; i<ds_list2.RowCount(); i++)
	{
		if(ds_list2.GetColumn(i,"POSIC") == "0" && ds_list2.GetColumn(i,"POSIT") == "0" && ds_list2.GetColumn(i,"POSIE") == "0")
		{
			gfn_showMsg("CW00191");
			ds_list2.row = i;
			return;
		}

		if(ds_list2.GetRowType(i) == "update")
		{
			var rows = ds_list3.AddRow();
			ds_list3.SetColumn(rows,"FLAG","I");
			ds_list3.SetColumn(rows,"LIFNR",ds_list2.GetColumn(i,"LIFNR"));
			ds_list3.SetColumn(rows,"PERNO",ds_list2.GetColumn(i,"PERNO"));
			ds_list3.SetColumn(rows,"NAME1",ds_list2.GetColumn(i,"NAME1"));
			ds_list3.SetColumn(rows,"NAMEC",ds_list2.GetColumn(i,"NAMEC"));
			ds_list3.SetColumn(rows,"NAMEE",ds_list2.GetColumn(i,"NAMEE"));
			ds_list3.SetColumn(rows,"NAMET",ds_list2.GetColumn(i,"NAMET"));
			ds_list3.SetColumn(rows,"GUBUN",ds_list2.GetColumn(i,"GUBUN"));
			ds_list3.SetColumn(rows,"GUBUN_O",ds_list2.GetOrgColumn(i,"GUBUN"));
			ds_list3.SetColumn(rows,"CELLP",ds_list2.GetColumn(i,"CELLP"));
			ds_list3.SetColumn(rows,"CELLP_O",ds_list2.GetOrgColumn(i,"CELLP"));
			ds_list3.SetColumn(rows,"POSIC",ds_list2.GetColumn(i,"POSIC"));
			ds_list3.SetColumn(rows,"POSIC_O",ds_list2.GetOrgColumn(i,"POSIC"));
			ds_list3.SetColumn(rows,"POSIT",ds_list2.GetColumn(i,"POSIT"));
			ds_list3.SetColumn(rows,"POSIT_O",ds_list2.GetOrgColumn(i,"POSIT"));
			ds_list3.SetColumn(rows,"POSIE",ds_list2.GetColumn(i,"POSIE"));
			ds_list3.SetColumn(rows,"POSIE_O",ds_list2.GetOrgColumn(i,"POSIE"));
			ds_list3.SetColumn(rows,"TEMNO",ds_list2.GetColumn(i,"TEMNO"));
			ds_list3.SetColumn(rows,"TEMNO_O",ds_list2.GetOrgColumn(i,"TEMNO"));
			ds_list3.SetColumn(rows,"BIGO",ds_list2.GetColumn(i,"BIGO"));
			ds_list3.SetColumn(rows,"TELNO_O",ds_list2.GetOrgColumn(i,"TELNO_O"));
			ds_list3.SetColumn(rows,"FAX_O",ds_list2.GetOrgColumn(i,"FAX_O"));
			ds_list3.SetColumn(rows,"ADDR",ds_list2.GetOrgColumn(i,"ADDR"));
			ds_list3.SetColumn(rows,"GBN",ds_list2.GetOrgColumn(i,"GBN"));
		}
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
          "ps13:PS1301001A_I2"
        , ""
        , ""
		);

	tit_callService(
        ""
        , ""
        , "ds_list5=ds_list3:A"
        , ""
        , ""
        , "fn_afterSave");
}
