/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list.RowCount()+"");

	ds_pm.Filter("LIFNR="+quote(ds_list.GetColumn(0,"LIFNR")));

	ds_list2.ClearData();
	if(ds_list.RowCount() > 0) fn_query2(0);
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery2(errCode, errMsg) {
	grd_list2.SetCellProp("head",1,"text",ds_list2.rowcount+" 명");
	//gfn_showMsg("CI00002",ds_list2.RowCount()+"");
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}
/********************************************************************************
* 기      능   : 조회 Callback  - 설치협력사 로그인시 협력업체코드 첫번째 선택
********************************************************************************/
function fn_afterLifnr(errCode, errMsg) {
	if( userGroup == '10' )
	{
		ds_lifnr.Filter("CODE=="+quote(G_VEND_CODE_E));
		cb_lifnr.Index = 0;
		cb_lifnr.Enable = false;
	}
	else
	{
		ds_lifnr.Filter("SO5==''");
		gfn_insertEmptyRow( ds_lifnr, G_CODE_ALL );
		cb_lifnr.value = "";
	}
}
