/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리
********************************************************************************/
function ds_list2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;

	//temno에 해당하는 팀장명을 얻기위해
	var rows1 = ds_pm.SearchRow("CODE == "+quote(ds_list2.GetColumn(nRow,"TEMNO")));
	var rows2 = ds_pm.SearchRow("CODE_NAME == "+quote(ds_list2.GetColumn(nRow,"NAMEE")));

	switch( strColumnID ) {
		case "POSIT":
			if(varNewValue == "0" && ds_list2.GetColumn(nRow,"NAMEE") == ds_pm.GetColumn(rows1,"CODE_NAME"))
			{
				gfn_showMsg("CW00192");
				ds_list2.SetColumn(nRow,"POSIT",ds_list2.GetOrgColumn(nRow,"POSIT"));
				break;
			}	
			else if(varNewValue == "1" && ds_list2.GetOrgColumn(nRow,"POSIT") == "1")
			{
				var temno = ds_list2.GetColumn(nRow,"TEMNO");
				ds_list2.SetColumn(nRow,"TEMNO",ds_list2.GetOrgColumn(nRow,"TEMNO"));
				for(var i=0; i<ds_list2.RowCount(); i++)
				{
					if(ds_list2.GetColumn(i,"TEMNO") == temno)
						ds_list2.SetColumn(i,"TEMNO",ds_list2.GetColumn(nRow,"TEMNO"));
				}
				break;
			}
			break;
		case "TEMNO":
			if(nRow == 0)
			{
				if(ds_list2.GetColumn(nRow,"POSIT") == "0" && ds_list2.GetOrgColumn(nRow,"POSIT") == "1" && 
				   ds_list2.GetColumn(nRow,"TEMNO") == ds_list2.GetOrgColumn(nRow,"TEMNO"))
				{
					ds_list2.SetColumn(nRow,"TEMNO",preTemno);
					break;
				}

				for(var i=1; i<ds_list2.RowCount(); i++)
				{
					if(ds_list2.GetColumn(i,"TEMNO") == ds_list2.GetOrgColumn(0,"TEMNO"))
						ds_list2.SetColumn(i,"TEMNO",ds_list2.GetColumn(0,"TEMNO"));
				}
				preTemno = ds_list2.GetColumn(0,"TEMNO");
				break;
			}	
	}		
	obj.fireevent = true;
}

/********************************************************************************
* 기      능   : 사업부 선택시 ds_lifnr 데이터셋 값 filtering
********************************************************************************/
function chk_so2_OnClick(obj,strValue)
{
	var so = ' ';
	var so2 = ' ';
	var so3 = ' ';
	var so4 = ' ';
	var so5 = ' ';
	var so7 = ' ';

	if(chk_so.Value == '1')  so =  'X';
	if(chk_so2.Value == '1') so2 = 'X';
	if(chk_so3.Value == '1') so3 = 'X';
	if(chk_so4.Value == '1') so4 = 'X';
	if(chk_so5.Value == '1') so5 = 'X';
	if(chk_so7.Value == '1') so7 = 'X';


	if(chk_so.Value+chk_so2.Value+chk_so3.Value+chk_so4.Value+chk_so5.Value+chk_so7.Value == '000000')
	{
		ds_lifnr.Filter("SO5=='' || CODE==''");
	}	
	else
	{
	  ds_lifnr.Filter("CODE=='' || SO=="+quote(so) +" || SO2=="+quote(so2) +" || SO3=="+quote(so3) +" || SO4=="+quote(so4) +" || SO5=="+quote(so5) +" || SO7=="+quote(so7));
	}

	cb_lifnr.Index = 0;	
}

/********************************************************************************
* 기      능   : 사무소 별 협력업체 필터 
********************************************************************************/
function cb_zzactss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    if(userGroup == "40" || userGroup == "30"){
    ds_cond.SetColumn(0,"LIFNR",''); }

    if(ds_cond.GetColumn(0,"ZZACTSS") == '')
    {
       ds_lifnr.UnFilter();
    }
    else
    {
	  var sZzactss = ds_cond.GetColumn(0,"ZZACTSS");
	  ds_lifnr.Filter("ZZACTSS=="+quote(sZzactss));
	}
}

/********************************************************************************
* 기      능   : 설치/구매 구분 별 협력업체 필터 
********************************************************************************/
function cb_clssfy_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(ds_cond.GetColumn(0,"CLSSFY") == '')
    {
       ds_lifnr.UnFilter();
    }
    else
    {
      var sClssfy  = ds_cond.GetColumn(0,"CLSSFY");

	  ds_lifnr.Filter("GBN ==" +quote(sClssfy));

    }
}
