function fn_OnLoadCompleted(obj) 
{
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	userGroup = gfn_getUserGroup();
	Tab00.tab1.ed_nation.Enable = false;
	//코드콤보 설정 : 업종구분,전공
	var codeArr = ["PS005","PS006","PS007"];
	var dsObjArr = [ds_sagbn,ds_upgbn,ds_major];
	var addTypeArr = [G_CODE_NONE,G_CODE_NONE,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S33");
	tit_callService(
		""
		, ""
		, "ds_cond_cd=ds_cond"
		, "ds_lifnr=ds_zzlifnr_cd"
		, ""
		, "fn_afterLifnr"
		, true,true);

		//alert(ds_major.SaveXML());		
	//trace(ds_major.SaveXML());
}
