/********************************************************************************
* 기      능   : 조회 - 기존에 등록되어있는 사원인지 조회
********************************************************************************/
function fn_selJumin(perkey) 
{

	ds_cond1.ClearData();
	ds_cond1.AddRow();

	//ds_cond1.SetColumn(0,"JUMIN",jumin);			
	ds_cond1.SetColumn(0,"PERKEY",perkey);

	ds_cond1.SetColumn(0,"LIFNR",ds_head.GetColumn(0,"LIFNR"));

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps13:PS1301004A_S4");
	tit_addSearchActionInfo("ps13:PS1301006A_S1");
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond1"
        , "ds_jumin=ds_jumin ds_black=ds_list"
        , ""
        , "fn_afterJumin"
        , "" //true
        , ""
        , true);




}

/********************************************************************************
* 기      능   : 조회 Callback  - 
********************************************************************************/
function fn_afterJumin(errCode, errMsg) 
{

    okcode = "";
	if(ds_jumin.RowCount() > 0)
	{	
		var arr = [ds_jumin.GetColumn(0,"NAME1"), ds_jumin.GetColumn(0,"POSI")];
		gfn_showMsg("CW00210",arr);
		//Tab00.tab1.med_jumin.Value = "";
		Tab00.tab1.ed_namee.Text = "";
		//Tab00.tab1.cal_birth.Value = "";
		return;
	}
	else if(ds_black.rowcount > 0)
	{	
		gfn_showMsg("CW00211",ds_black.GetColumn(0,"NAMEE"));
		//Tab00.tab1.med_jumin.Value = "";
		Tab00.tab1.ed_namee.Text = "";
		//Tab00.tab1.cal_birth.Value = "";
		return;
	}

	okcode = "OK";
}


