function fn_save() 
{
	if(Tab00.TabIndex == 0)
	{
		//fn_selJumin(perkey);		
		fn_selJumin(perkey);
		if(okcode != "OK")
		{	
			return;
		}

		ds_list.SetColumn(0,"STATS","A");
		fn_ipsa();	
	}
	else if(Tab00.TabIndex == 1)
	{
		ds_list2.SetColumn(0,"STATS","A");
		fn_reti();
	}

	/* if(length(ds_list.GetColumn(0, "BIRTH")) <=0 )
		{
			var arr = [parseInt(0) + 1, "BIRTH"];			
			gfn_showAlert("CW00224", arr);
			return;
		}*/ 
}
/********************************************************************************
* 기      능   : 저장 - 입사 
********************************************************************************/
function fn_save1() {
	if(!validCheck()) return;



	var arr = split(G_ORG_USER_INFO, "^", true);
	if(length( Tab00.tab1.ed_insuName.value ) > 0 ) {
		// 첨부 파일 처리 
		var arr = tit_fileWrite(
			Tab00.tab1.httpFile
			, "PS"
			, ds_list.getColumn(0, "INSU_FILE_PATH")
			, ds_list.getColumn(0, "PICINSU")
			, ""
			, ""
			, "" );  

		// 처리 결과 확인하기 
		if( arr[0] == "S" ) {
			// 성공 
			ds_list.setColumn(0, "PICINSU", arr[2]);	// 파일 명 
		} else {
			gfn_showAlert( arr[1] );
			return;
		}
	} else {
		ds_list.setColumn(0, "INSU_SIZE", 0);
	}

	if(length( Tab00.tab1.ed_tuName.value ) > 0 ) {
		// 첨부 파일 처리 
		var arr = tit_fileWrite(
			Tab00.tab1.httpFile
			, "PS"
			, ds_list.getColumn(0, "TU_FILE_PATH")
			, ds_list.getColumn(0, "PICTU")
			, ""
			, ""
			, "" );  

		// 처리 결과 확인하기 
		if( arr[0] == "S" ) {
			// 성공 
			ds_list.setColumn(0, "PICTU", arr[2]);	// 파일 명 
		} else {
			gfn_showAlert( arr[1] );
			return;
		}
	} else {
		ds_list.setColumn(0, "TU_SIZE", 0);
	}	

	if(Tab00.tab1.rdo_direct.Index == '0') 
	{
	  ds_list.SetColumn(0,"DIRECT","X");
	  ds_list.SetColumn(0,"INDIRECT",'');
	}
	else if(Tab00.tab1.rdo_direct.Index == '1')
	{
	 ds_list.SetColumn(0,"DIRECT",'');
	 ds_list.SetColumn(0,"INDIRECT","X");
	}
	//========================팀구분 저장========================
	//if(ds_list.getColumn(0, "ZTEAM") != "X")
	//{  
	//  ds_list.SetColumn(0,"ZTEAM","");	
	//}
//서울 사무소 코드화로 인한 주석처리	
/*	if(ds_list.getColumn(0, "ACTSS") == '02')
	{
		if(Tab00.tab1.rdo_team.Index == '1') //강남팀
		{
			ds_list.SetColumn(0,"ZTEAM","");
		}
		else if(Tab00.tab1.rdo_team.Index == '2') //강북팀
		{
			ds_list.SetColumn(0,"ZTEAM","X");
		}		
	}
	else
	{
		ds_list.SetColumn(0,"ZTEAM","");
	}
*/		
	//===========================================================

	//========================내국인/외국인 구분 저장========================

		if(Tab00.tab1.rdo_local.Index == '0') //내국인
		{
			ds_list.SetColumn(0,"LOCALMAN","X");

		}
		else //외국인
		{
			ds_list.SetColumn(0,"LOCALMAN",'');

		}		
	//===========================================================

	if(Tab00.tab1.chk_posic.Value == 'X' || Tab00.tab1.chk_posit.Value == 'X')
	{
		ds_list.SetColumn(0,"NAMET",Tab00.tab1.ed_namee.Text );
		ds_list.SetColumn(0,"TEMNO","" );
	}	
	else ds_list.SetColumn(0,"NAMET",Tab00.tab1.cb_pm.Text ); 

//alert("저장 전!");	
//==============여기서부터(fn_afterIpsa 이 펑션부터 확인해볼것    프로젝트, 프로젝트명 추가할것)
	tit_clearActionInfo();
	tit_addSelectExActionInfo("ps13:PS1301004A_S5", "ps13:PS1301004A_I1", "ps13:PS1301004A_U1");
	tit_callService(
		""
		, ""
		, "ds_list=ds_list"
		, ""
		, ""
		, "fn_afterSave"
		, true);

}
