/********************************************************************************
* 기      능   : validation check 
********************************************************************************/
function validCheck()
{
	//사업구분 필수체크	
	if(gfn_isNull(Tab00.tab1.rd_sagbn, true, trim(Tab00.tab1.st_sagbn.value))) return;
   //직무구분 필수체크	
	if(gfn_isNull(Tab00.tab1.rd_duty, true, trim(Tab00.tab1.st_duty.value))) return;
	//사무소 필수체크	
	if(gfn_isNull(Tab00.tab1.cb_zzactss, true, trim(Tab00.tab1.st_zzactss.value))) return;
	//업종구분 필수체크	
	if(gfn_isNull(Tab00.tab1.cb_upgbn, true, trim(Tab00.tab1.st_upgbn.value))) return;
	//성명 필수체크	
	if(gfn_isNull(Tab00.tab1.ed_namee, true, trim(Tab00.tab1.st_namee.value))) return;
	//경력 필수체크	
	if(gfn_isNull(Tab00.tab1.cal_fdate1, true, trim(Tab00.tab1.Static2.value))) return;
	if(gfn_isNull(Tab00.tab1.cal_tdate1, true, trim(Tab00.tab1.Static2.value))) return;
	if(gfn_isNull(Tab00.tab1.cb_item, true, trim(Tab00.tab1.Static2.value))) return;
