		gfn_showMsg("CW00207","보험현황");
		return;
	}
	//보험증권명 필수체크	
//	if(gfn_isNull(Tab00.tab1.ed_insunm, true, Tab00.tab1.st_insunm.value)) return;
	//보험증권번호 필수체크	
