	//보험증권 필수체크	
	if(gfn_isNull(Tab00.tab1.ed_insuName, true, Tab00.tab1.st_insuName.value)) return;
	//증명사진 필수체크	
	if(gfn_isNull(Tab00.tab1.ed_tuName, true, Tab00.tab1.st_tuName.value)) return;

	if(Tab00.tab1.cb_yy.Value != "00" || Tab00.tab1.cb_mm.Value != "00")
	{
		// 경력일자1 체크
		if ( !gfn_checkFromToObj(Tab00.tab1.cal_fdate1, Tab00.tab1.cal_tdate1, true, Tab00.tab1.st_fdate1.value, 8, true) ) {
			return;
		}
		// 경력일자2 체크
		if ( !gfn_checkFromToObj(Tab00.tab1.cal_fdate2, Tab00.tab1.cal_tdate2, true, Tab00.tab1.st_fdate2.value) ) {
			return;
		}
		// 경력일자3 체크
		if ( !gfn_checkFromToObj(Tab00.tab1.cal_fdate3, Tab00.tab1.cal_tdate3, true, Tab00.tab1.st_fdate3.value) ) {
			return;
		}
		// 경력일자4 체크
		if ( !gfn_checkFromToObj(Tab00.tab1.cal_fdate4, Tab00.tab1.cal_tdate4, true, Tab00.tab1.st_fdate4.value) ) {
			return;
		}
		// 경력일자5 체크
		if ( !gfn_checkFromToObj(Tab00.tab1.cal_fdate5, Tab00.tab1.cal_tdate5, true, Tab00.tab1.st_fdate5.value) ) {
			return;
		}
		// 경력일자6 체크
		if ( !gfn_checkFromToObj(Tab00.tab1.cal_fdate6, Tab00.tab1.cal_tdate6, true, Tab00.tab1.st_fdate6.value) ) {
			return;
		}
		//경력내용1
		if ( Tab00.tab1.cal_fdate1.Value > '' && Tab00.tab1.cb_item.Value = '') {
			gfn_showMsg("CW00001",st_item1.value);
			Tab00.tab1.ed_item1.SetFocus();
			return;
		}
		//경력내용2
		if ( Tab00.tab1.cal_fdate2.Value > '' && Tab00.tab1.cb_item2.Value = '') {
			gfn_showMsg("CW00001",st_item2.value);
			Tab00.tab1.ed_item2.SetFocus();
			return;
		}
		//경력내용3
		if ( Tab00.tab1.cal_fdate3.Value > '' && Tab00.tab1.cb_item3.Value = '') {
			gfn_showMsg("CW00001",st_item3.value);
			Tab00.tab1.ed_item3.SetFocus();
			return;
		}
		//경력내용4
		if ( Tab00.tab1.cal_fdate4.Value > '' && Tab00.tab1.cb_item4.Value = '') {
			gfn_showMsg("CW00001",st_item4.value);
			Tab00.tab1.ed_item4.SetFocus();
			return;
		}
		//경력내용5
		if ( Tab00.tab1.cal_fdate5.Value > '' && Tab00.tab1.cb_item5.Value = '') {
			gfn_showMsg("CW00001",st_item5.value);
			Tab00.tab1.ed_item5.SetFocus();
