/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "POSIC":
			if(varNewValue == 'X') ds_list.SetColumn(nRow,"POSIE",'');
			Tab00.tab1.cb_pm.Text = "";
			break;
		case "POSIT":
			if(varNewValue == 'X') ds_list.SetColumn(nRow,"POSIE",'');
			Tab00.tab1.cb_pm.Text = "";
			break;
		case "POSIE":
			if(varNewValue == 'X')
			{
				ds_list.SetColumn(nRow,"POSIC",'');
				ds_list.SetColumn(nRow,"POSIT",'');	
			}	
			break;
		case "INSU1":
			if(varNewValue == 'X') ds_list.SetColumn(nRow,"INSU1",'X');
			else  ds_list.SetColumn(nRow,"INSU1",'');
			break;
		case "INSU2":
			if(varNewValue == 'X') ds_list.SetColumn(nRow,"INSU2",'X');
			else  ds_list.SetColumn(nRow,"INSU2",'');
			break;
		case "INSU3":
			if(varNewValue == 'X') ds_list.SetColumn(nRow,"INSU3",'X');
			else  ds_list.SetColumn(nRow,"INSU3",'');
			break;
		case "INSU4":
			if(varNewValue == 'X') ds_list.SetColumn(nRow,"INSU4",'X');
			else  ds_list.SetColumn(nRow,"INSU4",'');
			break;
		case "MAJOR":
			if(varNewValue == '1' || varNewValue == '2' || varNewValue == '5' ) Tab00.tab1.rdo_direct.Index = 0;
			else if(varNewValue == '3') Tab00.tab1.rdo_direct.Index = 1;

			break;

	}

	obj.fireevent = true;	
}
