/********************************************************************************
* 기      능   : 주민번호 대체를 위하여 생년월일 체크시  // 2015.03.27 다시 추가(생년월일 잘못기입하는 경우가 늘어남)
********************************************************************************/
function Tab00_tab1_cal_birth_OnChanged(obj,strOldText,strNewText)
{
   var day;
   var birth;

//alert("a");
	perkey = strNewText + trim(Tab00.tab1.ed_namee.text);	
	ds_list.setColumn(0, "PERKEY", perkey);


	day = parseInt(substr(gfn_getCurrDate(),0,4))+1;
	birth = day - parseInt(substr(strNewText,0,4)) ;
	//trace(parseInt(gfn_getCurrDate()));	
	// trace(birth);

	if(birth<18)
	{ 
	  ds_list.setColumn(0, "BIRTH", '');
	  gfn_showMsg("CW10025","18세 이상으로 입력하세요");
//alert(perkey);	 

	 }
	 else if(birth>90) {
	  ds_list.setColumn(0, "BIRTH", '');
	  gfn_showMsg("CW10025","90세 이하로 입력하세요");
//alert(perkey);	   
	 }	
	 else {
	  ds_list.setColumn(0, "BIRTH", strNewText);
//alert("ok");		  

	 }
	// trace(ds_list.SaveXML());

}
