/********************************************************************************
* 기      능   : 보험증권 첨부파일 열기 
********************************************************************************/
function btn_openFile_OnClick(obj)
{
	Tab00.tab1.fileDialog.open();

	if ( length(Tab00.tab1.fileDialog.FilePath) == 0 
		|| length(Tab00.tab1.fileDialog.FileName ) == 0 ) {
		return;
	}

	var fileSize = Tab00.tab1.f_tmp.GetLength(Tab00.tab1.fileDialog.FilePath + "\\" + Tab00.tab1.fileDialog.FileName );
	if ( fileSize <= 0 ) {
		gfn_showAlert("CW00048");
		return;
	}

	if ( fileSize > toNumber(F_SIZE)) {
		gfn_showAlert("CW00047", "10 Mbytes" );	
		return;
	}

	//한글제목 입력금지(한글제목깨짐)
	for( var i = 0; i < length( Tab00.tab1.fileDialog.FileName); i++ ) {
		if ( lengthb(charAt( Tab00.tab1.fileDialog.FileName, i)) == 2 ) {
			gfn_showMsg("CW00213");
			return;
		}
	}

	ds_list.setColumn(0, "PICINSU", Tab00.tab1.fileDialog.FileName);
	ds_list.setColumn(0, "INSU_SIZE", fileSize);
	ds_list.setColumn(0, "INSU_FILE_PATH", Tab00.tab1.fileDialog.FilePath);
	ds_list.setColumn(0, "INSU_FILE_PATH", Tab00.tab1.fileDialog.FilePath);

	Tab00.tab1.ed_insuName.value = Tab00.tab1.fileDialog.FileName;
}
/********************************************************************************
* 기      능   : 보험증권 첨부파일 삭제 
********************************************************************************/
function btn_deleteFile_OnClick(obj)
{
	Tab00.tab1.ed_insuName.value = "";
	ds_list.setColumn(0, "PICINSU", "");
	ds_list.setColumn(0, "INSU_SIZE", 0);
	ds_list.setColumn(0, "INSU_FILE_PATH", "");
}
