{
	switch(obj) {
	case Tab00.tab2.cb_sawon:
		ds_zpst0147.SetColumn(ds_zpst0147.currow,"PERNO",strCode);
		break;

	case Tab00.tab2.cb_rsn1:
		ds_list2.SetColumn(ds_list2.currow,"RETIT",strText);

		Tab00.tab2.cb_rsn2.Value = '';
		Tab00.tab2.cb_rsn3.Value = '';
		ds_rsn2.Filter("DTEXT1="+quote(ds_rsn1.GetColumn(nNewIndex,"CODE1")));
		ds_rsn2.Filter("DTEXT2="+quote(ds_rsn1.GetColumn(nNewIndex,"CODE2")));
		ds_rsn3.Filter("DTEXT1="+quote(ds_rsn1.GetColumn(nNewIndex,"CODE1")));
		ds_rsn3.Filter("DTEXT2="+quote(ds_rsn1.GetColumn(nNewIndex,"CODE2")));
		break;

	default:
		break;
	}
}

function cal_retid_OnChanged(obj,strOldText,strNewText)
{
	ds_list2.SetColumn(ds_list2.currow,"RETID",strNewText);
}

function edit_Onchanged(obj,strText)
{
	switch(obj) {
	case Tab00.tab2.ed_rsn4:
		ds_list2.SetColumn(ds_list2.currow,"RETIT",strText);
		break;

	default:
		break;
	}
}
/********************************************************************************
* 기      능   : 국적  활성 / 비활성 
********************************************************************************/
function Tab00_tab1_rdo_local_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(Tab00.tab1.rdo_local.Value == '1')
	{
	   Tab00.tab1.ed_nation.Enable = false;
	} else
	{
	  Tab00.tab1.ed_nation.Enable = true;
	}
}
/********************************************************************************
* 기      능   : 직무에 따른 간접 / 직접인원
********************************************************************************/
function Tab00_tab1_rd_duty_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(Tab00.tab1.rd_duty.Value = 1){   //설치인 경우만 직접인원
	  Tab00.tab1.rdo_direct.index = 0;
      Tab00.tab1.chk_posit.Enable = false;
      Tab00.tab1.chk_posit.Value = 0 ;
	}
	else if(Tab00.tab1.rd_duty.Value = 2) // 관리인 경우 팀장 선택 가능
	{
	  Tab00.tab1.rdo_direct.index = 1;
	  Tab00.tab1.chk_posit.Enable = true;
	}
	else if(Tab00.tab1.rd_duty.Value = 3)
	{
	 Tab00.tab1.rdo_direct.index = 1;
     Tab00.tab1.chk_posit.Enable = false;
     Tab00.tab1.chk_posit.Value = 0 ;
	}


		    ds_cond5.ClearData();
	        ds_cond5.AddRow();
	        ds_cond5.SetColumn(0,"TEMNO",Tab00.tab1.cb_pm.Value);

         	tit_clearActionInfo();
	        tit_addSearchActionInfo("ps13:PS1301004A_S21");
	        tit_callService(
              ""
            , ""
            , "ds_cond5=ds_cond5"
            , "ds_dir=ds_dir"
            , ""
            , "fn_afterdir"
            , true);


}
