function fn_afterdir()
{
    //alert(ds_dir.GetColumn(0,"DIRECT"));
     if( ds_dir.GetColumn(0,"DIRECT") == '' && Tab00.tab1.rdo_direct.Index == 0  && ds_dir.GetColumn(0,"POSIC") <>'X')
   	  {
   	     gfn_showMsg("CW00022","선택하신 팀장의 경우 간접인원으로 직접인원 팀원 지정이 불가합니다.");
  	     ds_list.SetColumn(0,"TEMNO",'');        
  	   }
 }
