function fn_query() {
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps13:PS1301004A_S1");
	tit_addSearchActionInfo("ps13:PS1301005B_S1");
	tit_addSearchActionInfo("ps13:PS1301005B_S2");
	tit_addSearchActionInfo("ps13:PS1301005B_S5");
	tit_addSearchActionInfo("ps13:PS1301005A_S3"); //퇴사이력
	tit_addSearchActionInfo("ps13:PS1301005D_S2");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_head=ds_head ds_list_p=ds_list ds_gyul=ds_gyul ds_gyul2=ds_gyul2 ds_ret=ds_ret ds_list_p2=ds_list_p2"
        , ""
        , "fn_afterQuery"
        , true);
}
