/********************************************************************************
* 기      능   : 결재 전 데이터 체크 
********************************************************************************/
function fn_decide() {
	if(!gfn_confirm('결재를 선택했습니다.\n진행 하시겠습니까?')) return false;


   	tit_clearActionInfo();
	tit_addSearchActionInfo("ps13:PS1301005B_S6");
	tit_callService(
	  ""
	, ""
    , "ds_list=ds_list"
    , "ds_list3=ds_list3"
	, ""
	, ""//"ufn_afterdaesuCount"
	, ""
    , ""
	, true);	


    fn_Afterdecide2() ;
}

/********************************************************************************
* 기      능   : 결재
********************************************************************************/
function fn_Afterdecide2() {      

  if(ds_list3.rowcount == 1)
  {  //alert(ds_list3.rowcount);
     //alert("a");
     global.FRM_BOTTOM.img_msgType.imageId = "bottom_icon_caution";
	 global.FRM_BOTTOM.st_msg.color = "yellow";       
	 global.FRM_BOTTOM.st_msg.value = "이미 등록된 인원입니다.";

	  return false; 
  }
  else
  {//alert("b");
   // alert(ds_list3.rowcount);

	var dates = substr(gfn_getCurrDate(),0,4)+"."+substr(gfn_getCurrDate(),4,2)+"."+substr(gfn_getCurrDate(),6,2);
	tit_clearActionInfo();

	if(ds_cond.RowCount() > 0)
	{//    alert("c");
		ds_msgsend.DeleteAll();
		gfn_initDs(ds_msgsend, true);
		var desc = "";
		var titl = "";
		var accp = "";
		if(last > '') //마지막 결재자 결재시 
		{//    alert("d");
			desc = ds_head.GetColumn(0,"NAME1")+" "+ds_list_p.GetColumn(0,"NAMEE")+"님 "+dates+"팀으로 요청 완료 되었습니다.";
			titl = "인원관리 통보건";
			accp = ds_head.GetColumn(0,"USERNUMB");
			//erp인원테이블에 insert
			if(retif = '등록')
			{//  alert("e");
				ds_temno.ClearData();
				if(posit == 'X')
				{//  alert("f");
					tit_addSearchActionInfo("ps13:PS1301005B_S4",true);
					tit_addSearchActionInfo("ps13:PS1301005B_S3",true);
					tit_addSingleActionInfo("ps13:PS1301005A_I1");
					tit_addSingleActionInfo("ps13:PS1301005A_I2");
				}
				else
				{  //  alert("g");
					ds_temno.AddRow();
					ds_temno.SetColumn(0,"TEMNO",ds_list_p.GetColumn(0,"TEMNO"));
					tit_addSingleActionInfo("ps13:PS1301005A_I2");
				}

				//최종 결재시 ZPST0138의 입사일을 현재일로 업데이트 
				tit_addSingleActionInfo("ps13:PS1301005B_U3");
			}
		}
		if(next > '') //다음결재자 있을경우
		{ // alert("h");
			desc = ds_head.GetColumn(0,"NAME1")+" "+ds_list_p.GetColumn(0,"NAMEE")+"님 "+retif+"신청 결재바랍니다.";
			titl = "인원결재바랍니다";
			accp = next;
		}

		ds_msgsend.SetColumn(0,"MESGSEND",G_USER_ID);
		ds_msgsend.SetColumn(0,"MESGACCP",accp);
		ds_msgsend.SetColumn(0,"MESGTITL",titl);
		ds_msgsend.SetColumn(0,"MESGDESC",desc);

		tit_addSingleActionInfo("ps13:PS1301007B_I2");
	}

	tit_addSingleActionInfo("ps13:PS1301005B_U1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond:A ds_msgsend=ds_msgsend ds_list=ds_list_p ds_temno=ds_temno"
        , "ds_temno = ds_temno"
        , "GYUL="+quote(G_USER_NAME)
        , "fn_afterDecide");

    }
}
/********************************************************************************
* 기      능   : 반송
********************************************************************************/
function fn_sendback() {
	if(!gfn_confirm('반송을 선택했습니다.\n진행 하시겠습니까?')) return false;

	gfn_openPopup("PS1301005F",true);

	tit_clearActionInfo();
	if(ds_cond.RowCount() > 0)
	{
		ds_msgsend.DeleteAll();
		gfn_initDs(ds_msgsend, true);
		var desc = ds_head.GetColumn(0,"NAME1")+" "+ds_list_p.GetColumn(0,"NAMEE")+"님 요청이 반송되었습니다.\n\n    설치부에 문의 바랍니다.";
		ds_msgsend.SetColumn(0,"MESGSEND",G_USER_ID);
		ds_msgsend.SetColumn(0,"MESGACCP",ds_head.GetColumn(0,"USERNUMB"));
		ds_msgsend.SetColumn(0,"MESGTITL","인원관리 통보건");
		ds_msgsend.SetColumn(0,"MESGDESC",desc);

		tit_addSingleActionInfo("ps13:PS1301007B_I2");
	}

	tit_addSingleActionInfo("ps13:PS1301005B_U2");
	tit_addSingleActionInfo("ps13:PS1301005B_U1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond:A ds_msgsend=ds_msgsend"
        , ""
        , "GYUL='반송'"
        , "fn_afterDecide");
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterDecide(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		fn_btnCtrl('false'); //버튼컨트롤
		gfn_showMsg("CI00003");

		close();

		//fn_query();
	}
}
/********************************************************************************
* 기      능   : 버튼컨트롤
********************************************************************************/
function fn_btnCtrl(bools) {
	div_btn.btn_decide.enable = bools;
	div_btn.btn_sendback.enable = bools;
}
/********************************************************************************
* 기      능   : 첨부 파일 저장 -보험증권
********************************************************************************/
function btn_saveFile1_OnClick(obj)
{
	if ( length(ds_list_p.getColumn(0, "PICINSU")) > 0) {
		// 첨부 파일 처리 
		saveDialog.FileName = tit_getLocalFileName(ds_list_p.getColumn(0, "PICINSU"));
		if (saveDialog.open())
		{
			tit_fileRead(
				httpFile
				, "PS"
				, ds_list_p.getColumn(0, "PICINSU")
				, saveDialog.FilePath
				, saveDialog.FileName);
		}
	}
}
/********************************************************************************
* 기      능   : 첨부 파일 저장 - 증명사진
********************************************************************************/
function btn_saveFile2_OnClick(obj)
