	ds_cond.SetColumn(0,"LIFNR",lifnr);
	ds_cond.SetColumn(0,"PERNO",perno);

	if(arg == 'G' || ds_list.getcolumn(ds_list.row,"STATS") == 'C') div_btn.btn_save.enable = false;
	//코드콤보 설정 : 사업구분,업종구분
	var codeArr = ["PS003","PS005","PS006","PS007"];
	var dsObjArr = [ds_zzactss,ds_sagbn,ds_upgbn,ds_major];
	var addTypeArr = [G_CODE_NONE,G_CODE_NONE,G_CODE_NONE,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);


	tit_clearActionInfo();
	tit_addSearchActionInfo("ps13:PS1301001A_S3");
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_pm=ds_pm"
        , ""
        , "fn_afterInit"
        , true);

}
