/********************************************************************************
* 기      능   : 첨부가능 파일 확장자 정의
********************************************************************************/
function ufn_isImage(fileNm) {
	var s = toUpperCase(right( fileNm, 4));
	if ( s == ".JPG" || s == ".GIF" || s == ".BMP") {
		return true;
	} else if ( toUpperCase(right( fileNm, 5)) == ".JPEG") {
		return true;
	}

	return false;
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - 
********************************************************************************/
function ds_list_p_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "POSIC":
			if(varNewValue == 'X') ds_list_p.SetColumn(nRow,"POSIE",'');
			cb_pm.Text = "";
			break;
		case "POSIT":
			if(varNewValue == 'X') ds_list_p.SetColumn(nRow,"POSIE",'');
			cb_pm.Text = "";
			break;
		case "POSIE":
			if(varNewValue == 'X')
			{
				ds_list_p.SetColumn(nRow,"POSIC",'');
				ds_list_p.SetColumn(nRow,"POSIT",'');
				ds_list_p.SetColumn(nRow,"TEMNO",ds_list_p.GetOrgColumn(nRow,"TEMNO"));
			}	
			break;
		case "INSU1":
			if(varNewValue == 'X') ds_list_p.SetColumn(nRow,"INSU1",'X');
			else  ds_list_p.SetColumn(nRow,"INSU1",'');
			break;
		case "INSU2":
			if(varNewValue == 'X') ds_list_p.SetColumn(nRow,"INSU2",'X');
			else  ds_list_p.SetColumn(nRow,"INSU2",'');
			break;
		case "INSU3":
			if(varNewValue == 'X') ds_list_p.SetColumn(nRow,"INSU3",'X');
			else  ds_list_p.SetColumn(nRow,"INSU3",'');
			break;
		case "INSU4":
			if(varNewValue == 'X') ds_list_p.SetColumn(nRow,"INSU4",'X');
			else  ds_list_p.SetColumn(nRow,"INSU4",'');
			break;
		//case "MAJOR":
		//	if(varNewValue == '1' || varNewValue == '2' || varNewValue == '5' || varNewValue == '6') rdo_direct.Index = 0;
		//	else if(varNewValue == '3' || varNewValue == '4' || varNewValue == '7' || varNewValue == '8') rdo_direct.Index = 1;
	}

	obj.fireevent = true;	
}
