/********************************************************************************
* 기      능   : 보험증권 첨부파일 열기 
********************************************************************************/
function btn_openFile_OnClick(obj)
{
	fileDialog.open();

	if ( length(fileDialog.FilePath) == 0 
		|| length(fileDialog.FileName ) == 0 ) {
		return;
	}

	var fileSize = f_tmp.GetLength(fileDialog.FilePath + "\\" + fileDialog.FileName );
	if ( fileSize <= 0 ) {
		gfn_showAlert("CW00048");
		return;
	}

	if ( fileSize > toNumber(F_SIZE)) {
		gfn_showAlert("CW00047", "4 Mbytes" );	
		return;
	}

	//한글제목 입력금지(한글제목깨짐)
	for( var i = 0; i < length( fileDialog.FileName); i++ ) {
		if ( lengthb(charAt( fileDialog.FileName, i)) == 2 ) {
			gfn_showMsg("CW00213");
			return;
		}
	}

	ds_list_p.setColumn(0, "PICINSU", fileDialog.FileName);
	ds_list_p.setColumn(0, "INSU_SIZE", fileSize);
	ds_list_p.setColumn(0, "INSU_FILE_PATH", fileDialog.FilePath);

	ed_insuName.value = fileDialog.FileName;
}
/********************************************************************************
* 기      능   : 보험증권 첨부파일 삭제 
********************************************************************************/
function btn_deleteFile_OnClick(obj)
{
	ed_insuName.value = "";
	ds_list_p.setColumn(0, "PICINSU", "");
	ds_list_p.setColumn(0, "INSU_SIZE", 0);
	ds_list_p.setColumn(0, "INSU_FILE_PATH", "");
}
/********************************************************************************
* 기      능   : 증명사진 첨부파일 열기 
********************************************************************************/
function btn_openFile2_OnClick(obj)
{
	fileDialog.open();

	if ( length(fileDialog.FilePath) == 0 
		|| length(fileDialog.FileName ) == 0 ) {
		return;
	}

	var fileSize = f_tmp.GetLength(fileDialog.FilePath + "\\" + fileDialog.FileName );
	if ( fileSize <= 0 ) {
		gfn_showAlert("CW00048");
		return;
	}

	if ( fileSize > toNumber(F_SIZE)) {
		gfn_showAlert("CW00047", "4 Mbytes" );	
		return;
	}

	//한글제목 입력금지(한글제목깨짐)
	for( var i = 0; i < length( fileDialog.FileName); i++ ) {
		if ( lengthb(charAt( fileDialog.FileName, i)) == 2 ) {
			gfn_showMsg("CW00213");
			return;
		}
	}

	ds_list_p.setColumn(0, "PICTU", fileDialog.FileName);
	ds_list_p.setColumn(0, "TU_SIZE", fileSize);
	ds_list_p.setColumn(0, "TU_FILE_PATH", fileDialog.FilePath);

	ed_tuName.value = fileDialog.FileName;
}
