function btn_pjt_OnClick(obj)
{
	var arg = "pjt="+quote(ed_pjt.Text);

	gfn_openPopup("CS9601002A",true,arg);

	//Tab00.tab1.ed_pjt2.Text = ed_pjt.Text;
//alert(	ed_pjt.Text );
	fn_sitenm();
}


/********************************************************************************
* 기      능   : 프로젝트 명
********************************************************************************/
function fn_sitenm()
{

	ds_cond2.ClearData();
	ds_cond2.AddRow();

	//ds_cond2.SetColumn(0,"PSPID",ds_list_p.GetColumn(0,"PSPID"));
	ds_cond2.SetColumn(0,"PSPID",ed_pjt.Text);

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps13:PS1301004A_S15");	
	tit_callService(
        ""
        , ""
        , "ds_cond2=ds_cond2"
        , "ds_sitenm=ds_sitenm"
        , ""
        , ""
        , "" //true
        , ""
        , true);        

     ed_sitenm.Text = ds_sitenm.GetColumn(0,"ZSITE_NM");
}

function cb_duty_CanApply(obj,strText,strLookUpText,nIdxItem,bLikeLookUp)
{
  if(cb_duty.Value = 1){   //설치인 경우만 직접인원
	  rdo_direct.Index  = 0;
	}
	else{
	  rdo_direct.Index  = 1;
	}
}
