/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery2(errCode, errMsg) {
	gfn_showMsg("CI00002",ds_list_p.RowCount()+"");
//ALERT("5");	
	if(stats == 'C')
	{ //결재선 지정된경우
	    btn_openFile.Enable = false;
		div_btn.btn_save.enable = false;
		div_btn.btn_delete.enable = false;
		div_btn.btn_query.enable = false;
		btn_right.Enable = false;
		btn_left.Enable = false;
		btn_up.Enable = false;
		btn_down.Enable = false;
		gfn_showmsg("결재가 진행중입니다. 결재선을 재 지정할 수 없습니다.");
		//return;
	}
	else if(stats == 'A')
	{   //진행시 사진 수정 불가
	   btn_openFile.Enable = false;   
	}
	else if(retif == '삭제')
	{  //삭제 시 사진 수정 불가 
	   btn_openFile.Enable = false;   
	}
	//ALERT("A : "+ds_list_p.SetColumn(0,"PICINSUR",tit_getLocalFileName(ds_list_p.getColumn(0, "PICINSUR"))));
//ALERT("B : "+ds_list_p.SetColumn(0,"PICTUR",tit_getLocalFileName(ds_list_p.getColumn(0, "PICTUR"))));

	ds_list_p.SetColumn(0,"PICINSUR",tit_getLocalFileName(ds_list_p.getColumn(0, "PICINSUR")));
	ds_list_p.SetColumn(0,"PICTUR",tit_getLocalFileName(ds_list_p.getColumn(0, "PICTUR")));
    ds_list_p.SetColumn(0,"PICTUR2",tit_getLocalFileName(ds_list_p.getColumn(0, "PICTUR2"))); 
//ALERT("A : "+ds_list_p.getColumn(0, "PICTU"));


	var fa_serverFileName = ds_list_p.getColumn(0, "PICTU");
	if( ufn_isImage( fa_serverFileName ) ) 
	{

		if(IndexOf(tit_getServerDir("PS", fa_serverFileName),'localhost') > 0)
		{

			Image1.ImageID = tit_getServerDir("PS", fa_serverFileName);
		}
		else
		{

			var idx = IndexOf(tit_getServerDir("PS", fa_serverFileName),'/',10);
			Image1.ImageID = substr(tit_getServerDir("PS", fa_serverFileName),0,idx)+substr(tit_getServerDir("PS", fa_serverFileName),idx);

		}
	}

	cal_enter.Value = enter;

	if(retif == '삭제') {
		ds_zpst0147.SetColumn(ds_zpst0147.currow,"LIFNR",lifnr);
		ds_zpst0147.SetColumn(ds_zpst0147.currow,"PERNO",perno);
		ds_zpst0147.SetColumn(ds_zpst0147.currow,"LEAVEDT",enter);
		ds_zpst0147.SetColumn(ds_zpst0147.currow,"PS109",ds_list.getcolumn(ds_list.row,"PS109"));

		ds_rsn1.rowpos = ds_rsn1.FindRow("CODE",ds_zpst0147.getcolumn(ds_zpst0147.currow,"PS109"));
		combobox_OnChanged(cb_rsn1,cb_rsn1.Value,cb_rsn1.Text,0,ds_rsn1.currow);

		ds_zpst0147.SetColumn(ds_zpst0147.currow,"PS110",ds_list.getcolumn(ds_list.row,"PS110"));
		ds_zpst0147.SetColumn(ds_zpst0147.currow,"PS111",ds_list.getcolumn(ds_list.row,"PS111"));
		ds_zpst0147.SetColumn(ds_zpst0147.currow,"BIGO",ds_list.getcolumn(ds_list.row,"RSN4"));
	} else {
		cb_rsn1.Enable = false;
		cb_rsn2.Enable = false;
		cb_rsn3.Enable = false;
		ed_rsn4.Enable = false;
	}

}
/********************************************************************************
* 기      능   : 결재선 저장
********************************************************************************/
function fn_save() {
	if(retif == '삭제') 
	{
//ALERT("1");	
		//퇴사사유 필수체크	
		if(gfn_isNull(cb_rsn1, true, st_rsn.Value)) return;
		if(gfn_isNull(cb_rsn2, true, st_rsn.Value)) return;
		if(ds_rsn3.GetRowCount() <> 0)
			if(gfn_isNull(cb_rsn3, true, st_rsn.Value)) return;
	}
//ALERT("2");	

	tit_clearActionInfo();
	tit_addSingleActionInfo("ps13:PS1301007B_D1");

	if(ds_gyul.RowCount() > 0)
	{
//ALERT("3");	
		ds_msgsend.DeleteAll();
		gfn_initDs(ds_msgsend, true);
		var desc = name1+" "+namee+"님 "+retif+"신청 결재바랍니다.";
		ds_msgsend.SetColumn(0,"MESGSEND",G_USER_ID);
		ds_msgsend.SetColumn(0,"MESGACCP",ds_gyul.GetColumn(0,"GID"));
		ds_msgsend.SetColumn(0,"MESGTITL","인원결재바랍니다.");
		ds_msgsend.SetColumn(0,"MESGDESC",desc);

		tit_addMultiActionInfo("ps13:PS1301007B_I1");
		tit_addSingleActionInfo("ps13:PS1301007B_I2");
		if(retif == '삭제') 
		{
//ALERT("4");		
			tit_addSingleActionInfo("ps13:PS1301007B_U1");
		}
	}

//ALERT("5");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond ds_list=ds_gyul:A ds_msgsend=ds_msgsend ds_zpst0147=ds_zpst0147"//ds_list=ds_list_p"
        , ""
        , ""
        , "fn_afterSave3");
}
