function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		//fn_query();
		fn_query2();
	}
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave3(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {

       var old = ed_tuName.value;
       var new = ed_tuName2.value;   

       if(retif == '등록' && old <> new && stats == 'B') // 결재선 미지정 시 사진등록 변경
       { 
 	     if(length( ed_tuName.value ) > 0 ) {
		 // 첨부 파일 처리 
		  var arr = tit_fileWrite(
			httpFile
			, "PS"
			, ds_list_p.getColumn(0, "TU_FILE_PATH")
			, ds_list_p.getColumn(0, "PICTU")
			, ""
			, ""
			, "" );  

		// 처리 결과 확인하기 
		if( arr[0] == "S" ) {
			// 성공 
			ds_list_p.setColumn(0, "PICTU", arr[2]);	// 파일 명 
			tit_addSingleActionInfo("ps13:PS1301007B_U2");
			tit_callService(
              ""
            , ""
            , "ds_list=ds_list_p"
            , ""
            , ""
            , "fn_afterSave");
		  } else {
		  	gfn_showAlert( arr[1] );
			return;
		}
	} else {
		ds_list_p.setColumn(0, "TU_SIZE", 0);
	}	
  }

	}
}
