/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
//	ds_list2.Clear();

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps13:PS1301009A_S1");
	tit_addSearchActionInfo("ps13:PS1301001A_S3");

	ds_cond.SetColumn(0,"YYMM",ds_cond.GetColumn(0,"YEAR")+ds_cond.GetColumn(0,"MONTH"));


	ds_cond.SetColumn(0,"SO_GBN","");
    arg = "";

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list ds_pm=ds_pm"
        , ""//arg
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {

if(ds_list.FindRow("CHK", 1)==-1){
	gfn_showMsg('CW00046');
	return; 
  }

    ds_list.Filter("CHK==1");
    ds_list2.ClearData();
	ds_list2.copyF(ds_list);
	ds_list.unFilter();	
    tit_clearActionInfo();					
	//tit_addSelectExActionInfo("ps13:PS1301009A_S2", , "ps13:PS1301009A_U1");
	tit_addSingleActionInfo("ps13:PS1301009A_U1");
	tit_addSingleActionInfo("ps13:PS1301009A_U2");
 	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true  // ""

		);
}
